r++;__N__		$error_messages[] = $weblangs->trans(\"fieldRequired\", $weblangs->trans(\"firstname\"));__N__	}__N__	__N__	if (ctype_alpha(str_replace(array(\"\\n\", \"\\t\", \' \'), \'\', $customer_firstname)) === false) {__N__	    $error++;__N__        $error_messages[] = $weblangs->trans(\"firstnameContainsLettersOnly\");__N__    }__N__	__N__	if (empty($customer_lastname)) {__N__		$error++;__N__		$error_messages[] = $weblangs->trans(\"fieldRequired\", $weblangs->trans(\"lastname\"));__N__	}__N__	__N__	if (ctype_alpha(str_replace(array(\"\\n\", \"\\t\", \' \'), \'\', $customer_lastname)) === false) {__N__	    $error++;__N__        $error_messages[] = $weblangs->trans(\"lastnameContainsLettersOnly\");__N__    }__N__	__N__	if (!empty($customer_passwd) && !empty($customer_new_passwd)) {__N__	    $id_website = $website->id;__N__        __N__        $sql  = \"select lsa.rowid, lsa.pass_encoding, lsa.pass_crypted, ls.nom, fk_soc \";__N__        $sql .= \"from \".MAIN_DB_PREFIX.\"societe_account lsa \";__N__        $sql .= \"left join \".MAIN_DB_PREFIX.\"societe ls on ls.rowid = lsa.fk_soc \";__N__        $sql .= \"where lsa.login = \'\". $customer_email .\"\' \";__N__        $sql .= \"and lsa.fk_website = \". $id_website . \" \";__N__        $sql .= \"limit 1\";__N__        __N__        $result = $db->query($sql);__N__		$objsql = $db->fetch_object($result);__N__        if (!empty($objsql)) {__N__    		if ($objsql->pass_encoding == \'dolibarr\') {__N__    			$correct_password = dol_verifyHash ($customer_passwd, $objsql->pass_crypted);__N__    		}__N__    		if ($objsql->pass_encoding == \'dolistore\') {__N__    		    $dolistore_key = getDolGlobalString(\"MARKETPLACE_SECRET_KEY\");__N__    		    $to_check = md5($dolistore_key.$customer_passwd);__N__    		    if ($objsql->pass_crypted === $to_check){__N__    		        $correct_password = 1;__N__    		    }__N__    		}__N__        }else{__N__            $error++;__N__    		$error_messages[] = $weblangs->trans(\"noValidAccount\")