e : ?>__N__        <?php print $current_cat_label; ?>__N__      <?php endif ?>__N__    </div>__N__  </section>__N__<?php else : ?>__N__  <?php if (!empty($search) || !empty($tag)) : ?>__N__    <section id=\"breadcrumb_section\" contenteditable=\"true\">__N__      <div class=\"breadcrumb clearfix\">__N__        <a class=\"home\" href=\"index.php\" title=\"<?php print $weblangs->trans(\"backHome\"); ?>\"><i class=\"icon-home\"></i></a>__N__        <?php if (!empty($tag) && $tag == \"Specials\") : ?>__N__          <span class=\"navigation-pipe\">&gt;</span> <?php print $weblangs->trans(\"priceDrop\"); ?>__N__        <?php else : ?>__N__          <span class=\"navigation-pipe\">&gt;</span> <?php print $weblangs->trans(\"search\"); ?>__N__        <?php endif ?>__N__      </div>__N__    </section>__N__  <?php endif ?>__N__<?php endif ?>__N__<!--  Breadcrumb  -->', '', 0);

