                  $files_and_images = getPublicFilesOfObject($product);__N__                                        foreach ($files_and_images as $key => $file) {__N__                                            if ($file[\'position\'] == 100) {__N__                                                $virtual_product_file = $file;__N__                                            }__N__                                            if ($file[\'position\'] == 0) {__N__                                                $image_cover = $file;__N__                                            }__N__                                            if (strpos($file[\'filename\'], \'image_\') !== false && strpos($file[\'filename\'], \'image_cover\') === false) {__N__                                                $otherimages[$key] = $file;__N__                                            }__N__                                        }__N__                                    }__N____N____N__                                    if (!empty($_POST) && $_POST[\'action\'] == \"new-module\") {__N__                                        foreach ($website_available_langs[\'langs\'] as $lang_key => $lang_label) {__N__                                            ${\'name_\' . $lang_key} = GETPOST(\'name_\' . $lang_key) ?? \'\';__N__                                            ${\'short_description_\' . $lang_key} = GETPOST(\'short_description_\' . $lang_key) ?? \'\';__N__                                            ${\'description_\' . $lang_key} = GETPOST(\'description_\' . $lang_key) ?? \'\';__N__                                        }__N____N____N__                                        $error = 0;__N__                                        $error_messages = array();__N____N__                                        foreach ($website_available_langs[\'langs\'] as $lang_key => $lang_label) {__N__                                            $product_name = GETPOST(\'name_\' . $lang_key);__N__            