INSERT INTO llx_website_page(rowid, fk_page, fk_website, pageurl, aliasalt, title, description, lang, image, keywords, status, date_creation, tms, import_key, grabbed_from, type_container, htmlheader, content, author_alias, allowed_in_frames) VALUES(17__+MAX_llx_website_page__, null, __WEBSITE_ID__, 'forgot-password', '', 'Forgot password', '', '', '', '', '1', '2024-07-19 16:32:47', '2024-09-20 22:00:07', null, '', 'page', '', '<?php // BEGIN PHP__N__session_start(); // start session__N__require_once DOL_DOCUMENT_ROOT . \'/categories/class/categorie.class.php\';__N__require_once DOL_DOCUMENT_ROOT . \'/core/lib/categories.lib.php\';__N__require_once DOL_DOCUMENT_ROOT.\'/core/class/html.formcompany.class.php\';__N__include_once DOL_DOCUMENT_ROOT.\'/societe/class/societeaccount.class.php\';__N__include_once DOL_DOCUMENT_ROOT.\'/core/lib/admin.lib.php\';__N__include_once DOL_DOCUMENT_ROOT.\'/core/modules/security/generate/modGeneratePassPerso.class.php\';__N__//includeContainer(\'_library_page_lib.php\');__N____N__$tag = GETPOST(\'tag\', \'alpha\');__N__$cat_id = GETPOST(\'cat\', \'alpha\');__N__$search = GETPOST(\'search_query\', \'alpha\');__N____N__$form = new Form($db);__N____N__// END PHP __N__?>__N__<div id=\"page\">__N__  <div class=\"header-container\">__N__    <?php includeContainer(\'header\'); ?>__N__  </div>__N__  <div class=\"columns-container\">__N__    <div id=\"columns\" class=\"container\">__N__        <!--  Breadcrumb  -->__N__        <div class=\"breadcrumb clearfix\">__N__        	<a class=\"home\" href=\"index.php\" title=\"Return to Home\"><i class=\"icon-home\"></i></a>__N__        	<span class=\"navigation-pipe\">&gt;</span> <?php print $weblangs->trans(\"resetPassword\"); ?>__N__        </div>__N__        <!--  Breadcrumb  -->__N__      <div id=\"slider_row\" class=\"row\">__N__        <div id=\"top_column\" class=\"center_column col-xs-12 col-sm-12\"></div>__N__      </div>__N__      <div class=\"row\">__N__        <!-- include lefet sidebar container -->__N__        <?php __N__            includeContainer(\'sidebar\');__N__            $__PAGE__TITLE__ = $weblangs->trans(\"forgotPasswordPageTitle\");__N__        ?>__N__        <div id=\"center_column\" class=\"center_column col-xs-12 col-sm-9\">__N__          <div class=\"clearfix\">__N__                <div class=\"row\">__N__                    <div class=\"col-xs-12\">__N__                        <?php__N__                            $error = 0;__N__                            $error_messages = array();__N__                            __N__                            // Send reset password email__N__            			    if (!empty($_POST[\'email\'])) {__N____N__                                $customer_email = GETPOST(\'email\');__N____N__                            	if (!isValidEmail($customer_email)) {__N__                            		$error++;__N__                            		$error_messages[] = $weblangs->trans(\"emailInvalid\");__N__                            	}__N__                            	__N__                            	$sql  = \"select lsa.rowid, lsa.login, lsa.fk_soc \";__N__                                $sql .= \"from \".MAIN_DB_PREFIX.\"societe_account lsa \";__N__                                $sql .= \"where lsa.login = \'\". $customer_email .\"\' \";__N__                                $sql .= \"and lsa.fk_website = \". $website->id . \" \";__N__                                $sql .= \"limit 1\";__N__                                __N__                                $result = $db->query($sql);__N__                            	$objsql = $db->fetch_object($result);__N__                            	__N__                            	if (empty($objsql)) {__N__                            		$error++;__N__                            		$error_messages[] = $weblangs->trans(\"emailDoesNotExist\");__N__                                }__N__                            __N____N__                                if($error == 0){__N__                                    // Start of transaction__N__                                    $db->begin();__N__                                    __N__                                    // Generate a random password__N__                                    $pass_temp_crypted = dol_hash(date(\'Y-m-d H:i:s\').$objsql->rowid);__N__                                    __N__                                    $societeaccount = new SocieteAccount($db);__N__                                    $societeaccount->fetch($objsql->rowid);__N__                                    $societeaccount->id = $objsql->rowid;__N__                                    $societeaccount->pass_temp = $pass_temp_crypted;__N__                                    $societeaccount->date_last_reset_password = dol_now();__N__                            __N__                                    $result = $societeaccount->update($user);__N__                                    if ($result < 0) {__N__                            			$error++;__N__                            			$error_messages[] = $weblangs->trans(\"errorOccurred\");__N__                            		}__N__                            		__N__                            		if ($error == 0) {__N__                            		    __N__                            		    $sql  = \"select ls.nom \";__N__                                        $sql .= \"from \".MAIN_DB_PREFIX.\"societe ls \";__N__                                        $sql .= \"where ls.rowid = \'\". $objsql->fk_soc .\"\' \";__N__                                        $sql .= \"limit 1\";__N__                                        __N__                                        $result = $db->query($sql);__N__                                		$objsql_customer = $db->fetch_object($result);__N__                                		__N__                                		// Generate the link__N__                                        $protocol = (!empty($_SERVER[\'HTTPS\']) && $_SERVER[\'HTTPS\'] !== \'off\' || $_SERVER[\'SERVER_PORT\'] == 443) ? \"https://\" : \"http://\";__N__                                        $host = $_SERVER[\'HTTP_HOST\'];__N__                                        $requestUri = $_SERVER[\'REQUEST_URI\'];__N__                                        $currentUrl = $protocol . $host . $requestUri;__N__                                        __N__                                		// Check if the URL already contains GET parameters__N__                                        if (strpos($requestUri, \'?\') !== false) {__N__                                            // If the URL contains parameters, append the token with &__N__                                            $resetLink = $currentUrl . \"&token=\" . $pass_temp_crypted;__N__                                        } else {__N__                                            // If the URL does not contain parameters, append the token with ?__N__                                            $resetLink = $currentUrl . \"?token=\" . $pass_temp_crypted;__N__                                        }__N____N__                            		    // Send welcome email__N__                            		    $substitutionarray = array(__N__                            	            \'__USER_NAME__\' => $objsql_customer->nom,__N__                            	            \'__PASSWORD__RESET_LINK__\' => $resetLink__N__                            	        );__N__                                    	$email_sended = marketplace_send_email(getDolGlobalInt(\"MARKETPLACE_FORGOT_PASSWORD_EMAIL_TEMPLATE\"), $societeaccount, $objsql->login, $substitutionarray);__N__                                        __N__                                        $db->commit();__N__                            		} else {__N__                            		    $db->rollback();__N__                            		}__N__                                }__N__                            		__N__                            }__N____N____N__                            // Check token__N__                            if (!empty($_GET[\'token\'])) {__N__                                __N__                                $token = $_GET[\'token\'];__N__                                $sql  = \"select lsa.rowid, lsa.pass_encoding, lsa.pass_crypted, lsa.pass_temp, lsa.date_last_reset_password, ls.nom, fk_soc \";__N__                                $sql .= \"from \".MAIN_DB_PREFIX.\"societe_account lsa \";__N__                                $sql .= \"left join \".MAIN_DB_PREFIX.\"societe ls on ls.rowid = lsa.fk_soc \";__N__                                $sql .= \"where lsa.pass_temp = \'\". $token .\"\' \";__N__                                $sql .= \"and lsa.fk_website = \". $website->id . \" \";__N__                                $sql .= \"limit 1\";__N__                                __N__                                $result = $db->query($sql);__N__                        		$objsql = $db->fetch_object($result);__N__                                if (!empty($objsql)) {__N__                                    $dateTokenDateTime = new DateTime($objsql->date_last_reset_password);__N__                                    $now = new DateTime();__N__                                    $interval = $now->diff($dateTokenDateTime);__N__                                    __N__                                    if ($interval->days < 1 && $interval->h < 24 && $objsql->pass_temp == $token) {__N__                                        $correct_token = 1;__N__                                    } else {__N__                                        $error++;__N__                            		    $error_messages[] = $weblangs->trans(\"wrongOrExpiredToken\");__N__                                    }__N____N__                                }else{__N__                                    $error++;__N__                            		$error_messages[] = $weblangs->trans(\"wrongOrExpiredToken\");__N__                                }__N__                                __N__                            }__N__                            __N__                            // Change password__N__                            if (!empty($_GET[\'token\']) && !empty($_POST) && !empty($correct_token) && $correct_token == 1) {__N__                                $token = GETPOST(\'token\');__N__                                $new_passwd1 = GETPOST(\'new_passwd1\');__N__                                $new_passwd2 = GETPOST(\'new_passwd2\');__N__                                __N__                                if (empty($new_passwd1) || empty($new_passwd2)) {__N__                            		$error++;__N__                            		$error_messages[] = $weblangs->trans(\"bothFieldsRequired\");__N__                            	}__N__                            	__N__                            	if (!empty($new_passwd1) && !empty($new_passwd2) && $new_passwd1 != $new_passwd2) {__N__                            		$error++;__N__                            		$error_messages[] = $weblangs->trans(\"passwordsAreNotTheSame\");__N__                            	}__N____N__                                if ($error == 0) {__N__                                    $check_pass = new modGeneratePassPerso($db, $conf, $langs, $user);__N__                                	$is_valid = $check_pass->validatePassword($new_passwd1);__N__                                	if(!$is_valid){__N__                                	    $error++;__N__                                		$error_messages[] = $weblangs->trans(\"newPasswordCriteria\");__N__                                	}__N__                                }__N__                                __N__                                if ($error == 0) {__N__                                    $societeaccount = new SocieteAccount($db);__N__                                    $societeaccount->fetch($objsql->rowid);__N__                    				$societeaccount->pass_encoding = \"dolibarr\";__N__                    				$societeaccount->pass_crypted = dol_hash($new_passwd1);__N__                    				$ret_account = $societeaccount->update($user);__N__                    				if ($ret_account < 0) {__N__                    					$error++;__N__                    					$error_messages[] = $weblangs->trans(\"errorOccurred\");__N__                    				} else {__N__                    				    $passwordChanged = 1;__N__                    				}__N__                                }__N__                                __N__                            }__N__            			?>__N__            			__N__            			<?php__N__            			    // Choose content to display__N__                			$page_content = \"email_form\";__N__                			if (isset($email_sended) && $email_sended != 0) {__N__                			    $page_content = \"email_sent\";__N__                			}__N__                			if (!empty($_GET[\'token\'])) {__N__                			    $page_content = \"password_form\";__N__                			}__N__                			if (!empty($_GET[\'token\']) && !empty($passwordChanged) && $passwordChanged == 1) {__N__                			    $page_content = \"password_changed\";__N__                			}__N__            			?>__N__            			__N__            			<?php if ($page_content == \"email_sent\"): ?>__N__                        	<p>__N__                        	    <div class=\"account_creation\">__N__                        			<h3 class=\"page-subheading\"><?php print $weblangs->trans(\"resetPassword\"); ?></h3>__N__                        	        <p class=\"p_with_margin box\"><?php print $weblangs->trans(\"resetPasswordEmailSent\"); ?></p>__N__                        	    </div>__N__                        	</p>__N__                        <?php endif; ?>__N____N____N__                        <?php if ($page_content == \"password_form\"): ?>__N__                            <h3 class=\"page-subheading\"><?php print