<div class=\'alert alert-danger\'>__N__            		<?php $add_s = ($error > 1) ? \"s\" : \"\"; ?>__N__            		<p><?php print $weblangs->trans(\"errorsOccurred\", $error, $add_s); ?></p>__N__            		<ol>__N__            		    <?php foreach ($error_messages as $key => $value): ?>__N__                    		<li><?php print $value; ?></li>__N__                    	<?php endforeach ?>__N__            		</ol>__N__            	</div>__N__        	<?php endif ?>__N__			<div class=\"form_content clearfix\">__N__				<div class=\"form-group\">__N__					<label for=\"email\"><?php print $weblangs->trans(\"email\"); ?></label>__N__					<input class=\"is_required validate account_input form-control\" data-validate=\"isEmail\" type=\"text\" id=\"email\" name=\"email\" value=\"<?php if (isset($customer_email)){print $customer_email;} ?>\">__N__				</div>__N__				<div class=\"form-group\">__N__					<label for=\"passwd\"><?php print $weblangs->trans(\"password\"); ?></label>__N__					<span><input class=\"is_required validate account_input form-control\" type=\"password\" data-validate=\"isPasswd\" id=\"passwd\" name=\"passwd\" value=\"<?php if (isset($customer_passwd)){print $customer_passwd;} ?>\"></span>__N__				</div>__N__				<input type=\"hidden\" name=\"action\" value=\"login\">__N__				<input type=\"hidden\" name=\"back\" value=\"<?php print $back; ?>\">__N__				<p class=\"lost_password form-group\"><a href=\"forgot-password.php\" title=\"<?php print $weblangs->trans(\"recoverPass\"); ?>\" rel=\"nofollow\"><?php print $weblangs->trans(\"forgotPassword\"); ?></a></p>__N__				<p class=\"submit\">__N__					<button type=\"submit\" id=\"SubmitLogin\" name=\"SubmitLogin\" class=\"button btn btn-default button-medium\">__N__						<span>__N__							<?php print $weblangs->trans(\"signIn\"); ?>__N__							<i class=\"icon-chevron-right right\"></i>__N__						</span>__N__					</button>__N__				</p>__N__			</div>__N__		</form>__N__</div>__N__', '', 0);

