INSERT INTO llx_website_page(rowid, fk_page, fk_website, pageurl, aliasalt, title, description, lang, image, keywords, status, date_creation, tms, import_key, grabbed_from, type_container, htmlheader, content, author_alias, allowed_in_frames) VALUES(24__+MAX_llx_website_page__, null, __WEBSITE_ID__, 'manage-products', '', 'manage-products', '', '', '', '', '1', '2024-07-03 08:34:43', '2024-10-17 17:04:36', null, '', 'page', '', '<?php // BEGIN PHP__N__session_start(); // start session__N__require_once DOL_DOCUMENT_ROOT . \'/categories/class/categorie.class.php\';__N__require_once DOL_DOCUMENT_ROOT . \'/core/lib/categories.lib.php\';__N__include_once DOL_DOCUMENT_ROOT.\'/societe/class/societeaccount.class.php\';__N__require_once DOL_DOCUMENT_ROOT.\"/commande/class/commande.class.php\";__N____N__/*print \"<pre>\";__N__print_r($_SERVER);__N__print \"</pre>\";__N__exit();*/__N____N__$tag = GETPOST(\'tag\', \'alpha\');__N__$cat_id = GETPOST(\'cat\', \'alpha\');__N__$search = GETPOST(\'search_query\', \'alpha\');__N__$current_lang = $_SESSION[\'lang\'][\'code\'];__N__$weblangs->load(\"products\");__N____N____N__// If not login redirect to login.__N__if(empty($_SESSION[\'user\'][\'id_user\'])){__N__    redirectToContainer(\'login-register\');__N__}__N____N____N__// Get Account ID.__N__$user_id = $_SESSION[\'user\'][\'id_user\'];__N__$customer_account = new SocieteAccount($db);__N__$result = $customer_account->fetch($user_id);__N__$customer_id = $customer_account->fk_soc;__N____N__// Get customer ID.__N__$customer = new Societe($db);__N__$result = $customer->fetch($customer_id);__N____N__// Get liste of products__N__$products_ids = array();__N____N__$sql = \'SELECT pf.fk_product, pf.datec \';__N__$sql .= \'FROM \'.MAIN_DB_PREFIX.\'product_fournisseur_price AS pf, \'.MAIN_DB_PREFIX.\'categorie_product AS cp \';__N__$sql .= \'WHERE pf.fk_soc = \'.((int) $customer_id).\' \';__N__$sql .= \'AND cp.fk_product = pf.fk_product \';__N__$sql .= \'AND cp.fk_categorie = \'.getDolGlobalInt(\"MARKETPLACE_ROOT_CATEGORY_ID\").\' \';__N__$sql .= \'GROUP BY pf.fk_product \';__N__$sql .= \'ORDER BY pf.datec ASC\';__N____N__if ($result_products = $db->query($sql)) {__N__	while ($product_id = $result_products->fetch_object()) {__N__        $products_ids[] = $product_id->fk_product;__N__	}__N__}__N____N__// END PHP __N__?>__N__<div id=\"page\">__N__  <div class=\"header-container\">__N__    <?php __N__        includeContainer(\'header\'); __N__        $__PAGE__TITLE__ = $weblangs->trans(\"manageProductsPageTitle\");__N__    ?>__N__  </div>__N____N__  <div class=\"columns-container\">__N__    <div id=\"columns\" class=\"container\">__N__        <!--  Breadcrumb  -->__N__        <div class=\"breadcrumb clearfix\">__N__        	<a class=\"home\" href=\"index.php\" title=\"Return to Home\"><i class=\"icon-home\"></i></a>__N__        	<span class=\"navigation-pipe\">&gt;</span> <a href=\"myaccount.php\" title=\"<?php print $weblangs->trans(\"myAccount\"); ?>\" data-gg=\"\"><?php print $weblangs->trans(\"manageProducts\"); ?></a>__N__        	<span class=\"navigation-pipe\">&gt;</span> <?php print $weblangs->trans(\"ListOfProductsServices\"); ?>__N__        </div>__N__        <!--  Breadcrumb  -->__N__      <div id=\"slider_row\" class=\"row\">__N__        <div id=\"top_column\" class=\"center_column col-xs-12 col-sm-12\"></div>__N__      </div>__N__      <div class=\"row\">__N__        <!-- include lefet sidebar container -->__N__        <?php includeContainer(\'sidebar\'); ?>__N__        <div id=\"center_column\" class=\"center_column col-xs-12 col-sm-9\">__N__            <?php if (empty($products_ids)): ?>__N__                <h1 class=\"page-heading\"><?php print $weblangs->trans(\"ListOfProductsServices\"); ?></h1>__N__                <p class=\"alert alert-warning\"><?php print $weblangs->trans(\"noProductToDisplay\"); ?> </p>__N__            <?php else: ?>__N__                <div class=\"clearfix\">__N__                    <h1 class=\"page-heading\"><?php print $weblangs->trans(\"ListOfProductsServices\"); ?></h1>__N__                    <div id=\"block-history\" class=\"block-center\">__N__                        <div id=\"block-order-detail\" class=\"unvisible\" style=\"display: block;\">__N__                    		<div id=\"order-detail-content\" class=\"table_block table-responsive\">__N__                    			<table class=\"table table-bordered table-bordered-block-order-detail order-history\">__N__                    				<thead>__N__                    					<tr>__N__                    						<th class=\"item\"><?php print $weblangs->trans(\"coverImage\"); ?></th>__N__                    						<th class=\"item\"><?php print $weblangs->trans(\"product\"); ?></th>__N__                    						<th class=\"item\"><?php print $weblangs->trans(\"Nb\"); ?></th>__N__                    						<th class=\"item\" width=\"80\"><?php print $weblangs->trans(\"amountEarned\"); ?></th>__N__                    						<th class=\"last_item\"></th>__N__                    					</tr>__N__                    				</thead>__N__                    				<tbody>__N__                    					<!-- Customized products -->__N__                    					<!-- Classic products -->__N__                    					<?php foreach ($products_ids as $products_id): ?>__N__                    					    <?php__N__                    					        $weblangs->load(\"products\");__N__                                                // Get order object__N__                                                $product = new Product($db);__N__                                                $result = $product->fetch($products_id);__N__                                                __N__                                                // Get nbr of sales__N__                                                $sql = \"SELECT sum(d.qty) as qty, sum(d.total_ht) as total_ht\";__N__                                        		$sql .= \" FROM \".$db->prefix().\"commandedet as d, \".$db->prefix().\"commande as c, \".$db->prefix().\"societe as s\";__N__                                        		$sql .= \" WHERE c.rowid = d.fk_commande\";__N__                                        		$sql .= \" AND d.fk_product = \".((int) $product->id);__N__                                        		$sql .= \" AND c.fk_soc = s.rowid\";__N__                                        		$sql .= \" AND (c.facture = 1 OR c.note_private LIKE \'%Order imported%\')\";__N____N__                                        		$resql = $db->query($sql);__N__    		                                    $stats = $db->fetch_object($resql);__N__    		                                    __N__    		                                    // Get package__N__    		                                    $files_and_images = getPublicFilesOfObject($product);__N__            			                        foreach ($files_and_images as $key => $file) {__N__                                                    if ($file[\'position\'] == 100) {__N__                                                        $virtual_product_file = $file;__N__                                                    }__N__                                                }__N__                                            ?>__N__                        					<tr class=\"item\">__N__                        					    <td>__N__                        					        <img class=\"img-responsive\" src=\"<?php print getImagePublicURLOfObject($product,0); ?>\" alt=\"<?php print $product->label; ?>\" title=\"<?php print $product->label; ?>\" width=\"160\" itemprop=\"image\" style=\"margin: auto;\" />__N__                        					    </td>__N__                        					    <td>__N__                        						    <p>__N__                        						        <b><?php print $product->label; ?> <?php print $product->array_options[\'options_marketplace_module_version\']; ?> </b> __N__                        						        __N__                        						        <small> (<?php print $product->array_options[\'options_marketplace_min_version\']; ?> - <?php print $product->array_options[\'options_marketplace_max_version\']; ?>)</small>__N__                        						    </p>__N__                        						    <p>__N__                        						        <small class=\"cart_ref\">__N__                        						            <?php print $product->description; ?><br><br>__N__                        						            <?php print $weblangs->trans(\"CurrentProductPrice\"); ?>: <?php print price($product->price); ?> € <?php print $weblangs->trans(\"exclTax\"); ?>, <?php print price($product->price_ttc); ?> € <?php print $weblangs->trans(\"inclTax\"); ?> <br>__N__                                                            <?php print $weblangs->trans(\"ref\"); ?>: <?php print $product->ref; ?><br>__N__                                                            <?php print $weblangs->trans(\"status\"); ?>: <?php print ($product->status > 0) ? \'<span style = \"color: #28a745;\"> enabled </span>\' : \'<span style = \"color: #dc3545;\"> disabled </span>\'; ?><br>__N__                                                            <?php if (!empty($virtual_product_file[\'url\'])) { ?>__N__                                                                <?php print $weblangs->trans(\"file\"); ?>: <a href=\"<?php print $virtual_product_file[\'url\']; ?>\" target=\"_new\" ><?php print $virtual_product_file[\'filename\']; ?> <i class=\"icon-cloud-download\"></i></a><br>__N__                                                            <?php } ?>__N__                                                            <?php print $weblangs->trans(\"date\"); ?>: <?php print dol_print_date($product->date_creation, \'day\'); ?><br>__N__                        						        </small>__N__                        						    </p>__N__                        						</td>__N__                        						<td>__N__                        						    <p><?php print $stats->qty; ?></p>__N__    											</td>__N__                        						<td width=\"160\">__N__                        							<p><span class=\"price\"><?php print price($stats->total_ht); ?> €</span></p>__N__                        						</td>__N__                        						<td class=\"btn-table\">__N__                        						    <a href=\"edit-module-product.php?id=<?php print $product->id; ?>\" class=\"btn btn-default button button-small\"><span title=\"<?php print $weblangs->trans(\"edit\"); ?>\"> <i class=\"icon-edit product-table-icons\"></i></span></a>__N__                        						    <a href=\"product-statistics.php?id=<?php print $product->id; ?>\" class=\"btn btn-default button button-small\"><span title=\"<?php print $weblangs->trans(\"listOfSells\"); ?>\"> <i class=\"icon-shopping-cart product-table-icons\"></i></span></a>__N__                        						</td>__N__                        					</tr>__N__                    					<?php endforeach ?>__N__                    				</tbody>__N__                    			</table>__N__                    		</div>__N__                        </div>__N__                    </div>__N__                    <ul class=\"footer_links clearfix\">__N__                    	<li>__N__                    		<a class=\"btn btn-default button button-small\" href=\"myaccount.php\">__N__                    			<span>__N__                    				<i class=\"icon-chevron-left\"></i> <?php print $weblangs->trans(\"backToYourAccount\"); ?>__N__                    			</span>__N__                    		</a>__N__                    	</li>__N__                    	<li>__N__                    		<a class=\"btn btn-default button button-small\" href=\"index.php\">__N__                    			<span><i class=\"icon-chevron-left\"></i> <?php print $weblangs->trans(\"home\"); ?></span>__N__                    		</a>__N__                    	</li>__N__                    </ul>__N__                </div>__N__            <?php endif ?>__N__          __N__        </div>__N__      </div>__N__    </div>__N__  </div>__N__  <!-- .columns-container -->__N__  <div class=\"footer-container\">__N__    <?php includeContainer(\'footer\'); ?>__N__  </div>__N__</div>', '', 0);

