                                          } else {__N__    												$payment_method = \"/\";__N__    												$invoice_pdf_link = \"#\";__N__                                                }__N__                                            ?>__N__                        					<tr class=\"item\">__N__                        					    <td>__N__                        						    <p><small class=\"cart_ref\"><?php print $order_object->ref; ?></small>__N__                        						</td>__N__                        						<td class=\"order-history\">__N__                        						    <?php $order_lines = $order_object->lines; ?>__N__                        						    <ul class=\"order-history-products-list\">__N__                        						    <?php foreach ($order_lines as $order_line): ?>__N__                        			                    <li><?php print $order_line->desc; ?></li>__N__    												<?php endforeach ?>__N__    												</ul>__N__    											</td>__N__                        						<td>__N__    												<p><?php print dol_print_date($order_object->date, \'day\'); ?></p>__N__    											</td>__N__                        						<td>__N__                        							<p><span class=\"price\"><?php print price($order_object->total_ttc); ?> €</span></p>__N__                        						</td>__N__                        						<td>__N__                        						    <p><?php print $payment_method; ?></p>__N__                        						</td>__N__                        						<td>__N__                        						    <p class=\"invoice-pdf-link\">__N__                                            			<i class=\"icon-file-text\"></i>__N__                                            			<a target=\"_blank\" href=\"<?php print $invoice_pdf_link; ?>\"> <?php print $weblangs->trans(\"invoicePdf\"); ?></a>__N__                                            		</p>__N__                        						</td>__N__                        						<td class=\"btn-table\">__N__                        						    <a href=\"order-details.php?ref=<?php print $order_object->ref; ?>\" class=\"btn btn-default button button-small\"><span><?php print $weblangs->trans(\"details\"); ?> <i class=\"icon-chevron-right right\"></i></span></a>__N__                        						</td>__N__                        					</tr>__N__                    					<?php endforeach ?>__N__                    				</tbody>__N__                    			</table>__N__                    		</div>__N__                        </div>__N__                    </div>__N__                    <ul class=\"footer_links clearfix\">__N__                    	<li>__N__                    		<a class=\"btn btn-default button button-small\" href=\"myaccount.php\">__N__                    			<span>__N__                    				<i class=\"icon-chevron-left\"></i> <?php print $weblangs->trans(\"backToYourAccount\"); ?>__N__                    			</span>__N__                    		</a>__N__                    	</li>__N__                    	<li>__N__                    		<a class=\"btn btn-default button button-small\" href=\"index.php\">__N__                    			<span><i class=\"icon-chevron-left\"></i> <?php print $weblangs->trans(\"home\"); ?></span>__N__                    		</a>__N__                    	</li>__N__                    </ul>__N__              </div>__N__          __N__            <?php endif ?>__N__        </div>__N__      </div>__N__    </div>__N__  </div>__N__  <!-- .columns-container -->__N__  <div class=\"footer-container\">__N__    <?php includeContainer(\'footer\'); ?>__N__  </div>__N__</div>', '', 0);

