x\">__N____N__                        <?php__N__                            if(!empty($_SESSION[\'products\'])){__N__                                __N__                                include_once DOL_DOCUMENT_ROOT.\'/societe/class/societeaccount.class.php\';__N__                                require_once DOL_DOCUMENT_ROOT.\"/commande/class/commande.class.php\";__N____N__                                // Load user and its permissions__N__                                $result = $user->fetch(\'\', \'admin\');	// Load user for login \'admin\'. Comment line to run as anonymous user.__N__                                if (!$result > 0) {__N__                                	dol_print_error(null, $user->error);__N__                                	exit;__N__                                }__N__                                $user->getrights();__N__                                __N__                                $user_id = $_SESSION[\'user\'][\'id_user\'];__N__                                $customer = new SocieteAccount($db);__N__                                $result = $customer->fetch($user_id);__N__                                $customer_id = $customer->fk_soc;__N__                                if (empty($customer_id)) {__N__                                	dol_print_error(null, $customer->error);__N__                                	exit;__N__                                }__N__                                __N__                                $com = new Commande($db);__N__                            __N__                                $com->socid          = $customer_id;__N__                                $com->date           = dol_now();__N__                                $com->note_private   = \'Order from the marketplace\';__N__                                $com->module_source  = \'Marketplace\';__N__                                $com->remise_percent = 0;__N__                                __N__                                $db->begin();__N__                                __N__                                // Check if there is an order for this user.__N__                                $sql_find_order = \"SELECT c.rowid, c.fk_statut, c.fk_soc, c.date_creation, c.module_source \";__N__                                $sql_find_order .= \"FROM \".MAIN_DB_PREFIX.\"commande as c \";__N__                                $sql_find_order .= \"WHERE c.fk_soc = \" . $customer_id . \" and c.fk_statut= 1 and c.facture = 0 and c.module_source = \'Marketplace\' \";__N__                                $sql_find_order .= \"order by c.date_creation desc \";__N__                                $sql_find_order .= \"limit 1\";__N__                                __N__                                $find_order = $db->query($sql_find_order);__N__                                $obj_order = $db->fetch_object($find_order);__N__                                __N__                                // If there is an order, update the current unpaid order lines by SESSION articles.__N__                                if (!empty($obj_order)) {__N__                                    $com->fetch($obj_order->rowid);__N__                                    $com->fetch_lines();__N__                                    $lines = $com->lines;__N__                                    $com->setDraft($user);__N__                                    __N__                                    // remove current order lines __N__                                    foreach ($lines as $line){__N__                                        $ree = $com->deleteLine($user,$line->id);__N__                                        //print $com->error . \'<br>\';__N__                                    }__N__                                    //exit();__N__                                } else {__N__                                    $result = $com->create($user);__N__                                }__N__                                __N__                                if ($result >= 0) {__N__                                    foreach ($_SESSION[\'products\'] as $session_product) {__N__                                        // Get  product__N__                                        $product = new Product($db);__N__                                        $result = $product->fetch($session_product[\'product_id\']);__N__                                        if (!$result > 0) {__N__                                        	dol_print_error($db, $product->error);__N__                                        	exit;__N__                                        }__N__                                        __N__                                        // Get seller__N__                                        $sql_get_seller_id = \"select lpfp.fk_soc __N__                                            from \".MAIN_DB_PREFIX.\"product_fournisseur_price lpfp __N__                                            where lpfp.fk_product =\" .$session_product[\'product_id\'] . \" LIMIT 1\";__N__                                        $resql = $db->query($sql_get_seller_id);__N__                                		$objsql = $db->fetch_object($resql);__N__                                		__N__                                		if(!empty($objsql->fk_soc)){__N__                                		    $thirdparty_seller = new Societe($db);__N__                                            $thirdparty_seller->fetch($objsql->fk_soc);__N__                                		}else{__N__                                		    // To complete with default third party __N__                                		}__N__                                		__N__                                		// Get buyer__N__                                	    $thirdparty_buyer = new Societe($db);__N__                                        $result = $thirdparty_buyer->fetch($customer_id);__N__                                        if (!$result > 0) {__N__                 