 $db->fetch_object($resql);__N__if ($is_owner->prices == 0) {__N__    redirectToContainer(\'manage-products\');__N__}__N__// END PHP __N__?>__N__<div id=\"page\">__N__  <div class=\"header-container\">__N__    <?php __N__        includeContainer(\'header\');__N__        $__PAGE__TITLE__ = $weblangs->trans(\"productStatisticsPageTitle\");__N__    ?>__N__  </div>__N____N__  <div class=\"columns-container\">__N__    <div id=\"columns\" class=\"container\">__N__        <!--  Breadcrumb  -->__N__        <div class=\"breadcrumb clearfix\">__N__        	<a class=\"home\" href=\"index.php\" title=\"Return to Home\"><i class=\"icon-home\"></i></a>__N__        	<span class=\"navigation-pipe\">&gt;</span> <a href=\"myaccount.php\" title=\"<?php print $weblangs->trans(\"myAccount\"); ?>\" data-gg=\"\"><?php print $weblangs->trans(\"manageProducts\"); ?></a>__N__        	<span class=\"navigation-pipe\">&gt;</span> <?php print $weblangs->trans(\"orderHistory\"); ?>__N__        </div>__N__        <!--  Breadcrumb  -->__N__      <div id=\"slider_row\" class=\"row\">__N__        <div id=\"top_column\" class=\"center_column col-xs-12 col-sm-12\"></div>__N__      </div>__N__      <div class=\"row\">__N__        <!-- include lefet sidebar container -->__N__        <?php includeContainer(\'sidebar\'); ?>__N__        <div id=\"center_column\" class=\"center_column col-xs-12 col-sm-9\">__N__          <div class=\"clearfix\">__N__                <?php__N__                    // Get product object__N__                    $product = new Product($db);__N__                    $result = $product->fetch($product_id);__N__                ?>__N__                <h1 class=\"page-heading\"><?php print $weblangs->trans(\"product\"); ?> : <?php print $product->label; ?></h1>__N__                <div id=\"block-history\" class=\"block-center\">__N__                    <div id=\"block-order-detail\" class=\"unvisible\" style=\"display: block;\">__N__                		<div id=\"order-detail-content\" class=\"table_block table-responsive\">__N__                			<table class=\"table table-bordered table-bordered-block-order-detail order-history\">__N__                				<thead>__N__                					<tr>__N__                						<th class=\"item\" width=\"50\">#</th>__N__                						<th class=\"item\" width=\"150\"><?php print $weblangs->trans(\"orderRef\"); ?></th>__N__                						<th class=\"item\"><?php print $weblangs->trans(\"ThirdPartyName\"); ?></th>__N__                						<th class=\"item\" width=\"120\"><?php print $weblangs->trans(\"date\"); ?></th>__N__                						<th class=\"last_item\"><?php print $weblangs->trans(\"amountEarned\"); ?></th>__N__                					</tr>__N__                				</thead>__N__                				<tbody>__N__                				    <?php__N__                				    // Get list of orders for this product __N__                					$orders_for_product = \"SELECT c.rowid, c.ref, c.fk_statut, c.fk_soc, c.date_commande, c.module_source , d.total_ht \";__N__                                    $orders_for_product .= \"FROM \".MAIN_DB_PREFIX.\"commande as c, \".MAIN_DB_PREFIX.\"commandedet as d \";__N__                                    $orders_for_product .= \"WHERE c.rowid = d.fk_commande and d.fk_product = \" . ((int) $product->id) . \" and c.fk_statut IN (1,3) and (c.facture = 1 || c.ref_ext IS NOT NULL) and c.module_source = \'Marketplace\' \";__N__                                    if ($result_orders = $db->query($orders_for_product)) {__N__                                        $i = 1;__N__                                    	while ($order_details = $result_orders->fetch_object()) {__N__                                    	    if (!empty($conf->cache[\'thirdparty\'][$order_details->fk_soc])) {__N__                                    	        $customer = $conf->cache[\'thirdparty\'][$order_details->fk_soc];__N__                                    	    } else {__N__                                    	        $customer = new Societe($db);__N__                                    	        $customer->fetch($order_details->fk_soc);__N__                                    	    __N__                                    	        $conf->cache[\'thirdparty\'][$order_details->fk_soc] = $customer;__N__                                    	    }__N__                                    ?>__N__                    					<tr class=\"item\">__N__                    					    <td>__N__                    					        <p><?php print $i; ?></p>__N__                    					    </td>__N__                    					    <td>__N__                    					        <p><?php print $order_details->ref; ?></p>__N__                    					    </td>__N__                    					    <td>__N__                    						        <b><?php print $customer->name; ?></b><br>__N____N__                    						        <small class=\"cart_ref\">__N__                                                        <?php print $weblangs->trans(\"Email\"); ?>: <?php print $customer->email; ?><br>__N__                                                        <?php print $weblangs->trans(\"registredOn\"); ?>: <?php print dol_print_date($customer->date_creation, \'day\'); ?>__N__                    						        </small>__N____N__                    						</td>__N__                    						<td>__N__                    						    <p><?php print dol_print_date($order_details->date_commande, \'day\'); ?></p>__N__											</td>__N__                    						<td width=\"160\">__N__                    							<p><span class=\"price\"><?php print price($order_details->total_ht); ?> €</span></p>__N__                    						</td>__N__                    					</tr>__N__                					    <?php__N__                					    $i++;__N__                                    	}__N__                                    }__N__                                    ?>__N__                				</tbody>__N__                			</table>__N__                		</div>__N__                    </div>__N__                </div>__N__                <ul class=\"footer_links clearfix\">__N__                	<li>__N__                		<a class=\"btn btn-default button button-small\" href=\"myaccount.php\">__N__                			<span>__N__                				<i class=\"icon-chevron-left\"></i> <?php print $weblangs->trans(\"backToYourAccount\"); ?>__N__                			</span>__N__                		</a>__N__                	</li>__N__                	<li>__N__                		<a class=\"btn btn-default button button-small\" href=\"index.php\">__N__                			<span><i class=\"icon-chevron-left\"></i> <?php print $weblangs->trans(\"home\"); ?></span>__N__                		</a>__N__                	</li>__N__                </ul>__N__          </div>__N__        </div>__N__      </div>__N__    </div>__N__  </div>__N__  <!-- .columns-container -->__N__  <div class=\"footer-container\">__N__    <?php includeContainer(\'footer\'); ?>__N__  </div>__N__</div>', '', 0);

