INSERT INTO llx_website_page(rowid, fk_page, fk_website, pageurl, aliasalt, title, description, lang, image, keywords, status, date_creation, tms, import_key, grabbed_from, type_container, htmlheader, content, author_alias, allowed_in_frames) VALUES(41__+MAX_llx_website_page__, null, __WEBSITE_ID__, 'products-list-grid', '', 'product list grid', 'product list grid', '', '', '', '1', '2024-02-02 16:48:47', '2024-10-17 11:14:31', null, '', 'blogpost', '', '<script type=\"text/javascript\">__N__  var displayList = false;__N__  var page_name = \'tt\';__N__  var quickView = false;__N__  var request = window.location.href;__N__  var display = \"grid\";__N__</script>__N____N__<?php // BEGIN PHP__N____N__$current_lang = $_SESSION[\'lang\'][\'code\'];__N____N__$root_cat_object = new Categorie($db);__N____N__if(GETPOST(\'cat\', \'alpha\')){__N__	$root_category_id = GETPOST(\'cat\', \'alpha\');__N__}__N____N__if(GETPOST(\'extcat\', \'alpha\')){__N__	$root_category_extid = GETPOST(\'extcat\', \'alpha\');__N__}__N____N__$tag = GETPOST(\'tag\', \'alpha\');__N__$list = GETPOST(\'list\', \'alpha\');__N____N__$search_words = GETPOST(\'search_query\', \'alpha\');__N__if (!empty($search_words)) {__N__    $keywords = explode(\" \", $search_words);__N__    $request = \'\';__N__    $order = \'ORDER BY CASE \';__N____N__    foreach ($keywords as $key => $value) {__N__        if ($key != 0) {__N__            $request .= \" OR \";__N__        }__N__        $value = $db->escape($value);__N____N__        $request .= \"(ol.label LIKE \'%\" . $value . \"%\' OR ol.description LIKE \'\" . $value . \" %\'  OR ol.description LIKE \'% \" . $value . \" %\'  OR ol.description LIKE \'% \" . $value . \"\')\";__N____N__        // Order conditions__N__        $order .= \"WHEN ol.label LIKE \'\" . $value . \" %\' THEN 1 \";__N__        $order .= \"WHEN ol.label LIKE \'% \" . $value . \" %\' THEN 1 \";__N__        $order .= \"WHEN ol.label LIKE \'% \" . $value . \"\' THEN 1 \";__N__        __N__        $order .= \"WHEN ol.label LIKE \'%\" . $value . \"%\' THEN 2 \";__N__        __N__        $order .= \"WHEN ol.description LIKE \'\" . $value . \" %\' THEN 3 \";__N__        $order .= \"WHEN ol.description LIKE \'% \" . $value . \" %\' THEN 3 \";__N__        $order .= \"WHEN ol.description LIKE \'% \" . $value . \"\' THEN 3 \";__N____N__        //$order .= \"WHEN ol.description LIKE \'%\" . $value . \"%\' THEN 4 \";__N__    }__N__    __N__    // Finalize filter and order__N__    $filter = \"($request) AND (o.tosell = 1) \";__N__    $order .= \" END, o.datec DESC\";  // Complete the order clause__N__} else {__N__    $filter = \'o.tosell = 1\';__N__}__N____N__$page_no = GETPOST(\'p\', \'alpha\');__N__if(empty($page_no)) {__N__	$page_no = 1;__N__}__N____N__$limit = GETPOST(\'n\', \'alpha\');__N__if(empty($limit)) {__N__	$limit = 11;__N__}else{__N__	$limit = $limit -1;__N__}__N____N__$orderby = GETPOST(\'orderby\', \'alpha\');__N__switch ($orderby) {__N__	case \'price\':__N__		$sortfield = \'price_ttc\';__N__		break;__N__	case \'name\':__N__		$sortfield = \'label\';__N__		break;__N__	case \'reference\':__N__		$sortfield = \'ref\';__N__		break;__N__	default:__N__		$sortfield = \'datec\';__N__		break;__N__}__N____N__$orderway = GETPOST(\'orderway\', \'alpha\');__N__switch ($orderway) {__N__	case \'asc\':__N__		$sortorder = \"ASC\";__N__		break;__N__	case \'desc\':__N__		$sortorder = \"DESC\";__N__		break;__N__	default:__N__		$sortorder = \"DESC\";__N__		break;__N__}__N____N__if ($page_no == 1) {__N__	$offset = 0;__N__}else{__N__	$offset = ( $limit * ($page_no-1) )+($page_no - 1);__N__}__N____N__$id = null;__N__$type  = Categorie::TYPE_PRODUCT;__N__$is_for_sell = 1;__N____N__if (empty($tag)){__N__	if (isset($root_category_id) || isset($root_category_extid)){__N__	    if (isset($root_category_id)) {__N__	        $result = $root_cat_object->fetch($root_category_id,\"\");__N__	    } else {__N__	        $result = $root_cat_object->fetch(\'\', \'\', Categorie::TYPE_PRODUCT, $root_category_extid);__N__	    }__N__		__N__		if ($result <= 0) {__N__			redirectToContainer(\'404\');__N__		}__N__		$root_category_id = $root_cat_object->id;__N__		$root_category_label = $root_cat_object->label;__N__	}else{__N__		$result = $root_cat_object->fetch(getDolGlobalInt(\"MARKETPLACE_ROOT_CATEGORY_ID\"));__N__		if ($result <= 0) {__N__			redirectToContainer(\'404\');__N__		}__N__		$root_category_label = $root_cat_object->label;__N__		$root_category_id = $root_cat_object->id;__N__	}__N__}else{__N__    if($tag == \"Specials\"){__N__        $result = $root_cat_object->fetch(getDolGlobalInt(\"MARKETPLACE_SPECIAL_CATEGORY_ID\"));__N__    }else{__N__        $result = $root_cat_object->fetch(\"\",$tag);__N__    }__N__	__N__	if ($result <= 0) {__N__		dol_print_error($db, $root_cat_object->error); exit;__N__	}__N__	$root_category_label = $root_cat_object->label;__N__	$root_category_id = $root_cat_object->id;__N__}__N__$root_category_type = $root_cat_object->type;__N____N____N__if($root_category_id == getDolGlobalInt(\"MARKETPLACE_ROOT_CATEGORY_ID\") && empty($tag) && empty($search_words) && $list == \"new\"){__N__    $delay_for_new = (int) getDolGlobalInt(\'MARKETPLACE_DELAY_FOR_NEW\', 30);__N__    $dateTime = new \\DateTime();__N__    $interval = new \\DateInterval(\'P\'.$delay_for_new.\'D\');__N__    $dateTime->sub($interval);__N__    $request = \"o.datec > \'\" . $dateTime->format(\'Y-m-d H:i:s\') . \"\'\";__N__    $filter = \"(\".$request . \") And (o.tosell=1)\";__N__}__N____N__// END PHP ?>__N____N__<?php__N__// Count SQL__N__$countSql = \"SELECT c.fk_product \";__N__$countSql .= \"FROM llx_categorie_product as c, \";__N__$countSql .= \"llx_product as o, \";__N__$countSql .= \"llx_product_lang as ol \";__N__$countSql .= \"WHERE o.entity IN (1) AND c.fk_categorie = \".$root_category_id.\" AND c.fk_product = o.rowid AND ol.fk_product = o.rowid \";__N__$countSql .= \"AND ol.lang = \'\".$db->escape($current_lang).\"\' AND \";__N__$countSql .= $filter;__N____N__$resql = $db->query($countSql);__N__$count_prods = $resql->num_rows;__N____N__// PRODUCT SQL__N__$sql = \"SELECT c.fk_product as id, o.ref, o.ref_ext, ol.label, ol.description, o.datec, o.price_ttc \";__N__$sql .= \"FROM llx_categorie_product as c, \";__N__$sql .= \"llx_product as o, \";__N__$sql .= \"llx_product_lang as ol \";__N__$sql .= \"WHERE o.entity IN (1) AND c.fk_categorie = \".$root_category_id.\" AND c.fk_product = o.rowid AND ol.fk_product = o.rowid \";__N__$sql .= \"AND ol.lang = \'\".$db->escape($current_lang).\"\' AND \";__N__$sql .= $filter;__N____N__if ($sortfield == \'datec\' && $sortorder == \'DESC\' && !empty($search_words)) {__N__    $sql .= \" \" . $order;__N__} else {__N__    $sql .= $db->order($sortfield, $sortorder);__N__}__N____N__if ($limit > 0 || $offset > 0) {__N__	$sql .= $db->plimit($limit + 1, $offset);__N__}__N____N__$total_no_of_pages = ceil($count_prods / ($limit + 1));__N__$second_last = $total_no_of_pages - 1; // total pages minus 1__N__$previous_page = $page_no - 1;__N__$next_page = $page_no + 1;__N__$test = \"\";__N__?>__N__<?php if ($count_prods > 0 && (strlen($search_words) == 0 || strlen($search_words) > 1)): ?>__N__	<?php if (GETPOST(\'cat\', \'alpha\') != \'\' || GETPOST(\'extcat\', \'alpha\') != \'\'): ?>__N__	    <section id=\"category_info_section\" contenteditable=\"true\">__N__    		<div class=\"content_scene_cat\">__N__    		    <?php__N__        		    $__PAGE__TITLE__ = $root_cat_object->label;__N__                    $__PAGE__DESC__ = $root_cat_object->description;__N__    		    ?>__N__    			<!-- Category image -->__N__    			<div class=\"content_scene_cat_bg\">__N__    				<div class=\"cat_desc\">__N__    					<span class=\"category-name\"><?php print $root_cat_object->label; ?></span>__N__    					<div class=\"rte\">__N__    						<p><?php print $root_cat_object->description; ?></p>__N__    					</div>__N__    				</div>__N__    			</div>__N__    		</div>__N__		</section>__N__	<?php endif ?>__N____N__	<?php if (GETPOST(\