ge actuelle__N__                                if ($start_page > 1) {__N__                                    echo \"<li><a href=\'?p=1\'><span>1</span></a></li>\";__N__                                    if ($start_page > 2) {__N__                                        echo \"<li><span><span><a>...</a></span></span></li>\";__N__                                    }__N__                                }__N__                                for ($counter = $start_page; $counter <= $end_page; $counter++) {__N__                                    if ($counter == $page_no) {__N__                                        echo \"<li class=\'active\'><span><span>$counter</span></span></li>\";__N__                                    } else {__N__                                        echo \"<li><a href=\'?p=$counter\'><span>$counter</span></a></li>\";__N__                                    }__N__                                }__N__                                if ($end_page < $total_no_of_pages) {__N__                                    if ($end_page < $total_no_of_pages - 1) {__N__                                        echo \"<li><span><span><a>...</a></span></span></li>\";__N__                                    }__N__                                    echo \"<li><a href=\'?p=$total_no_of_pages\'><span>$total_no_of_pages</span></a></li>\";__N__                                }__N__                            }__N__                            ?>__N__            __N__                            <li class=\"pagination_next <?php if($page_no >= $total_no_of_pages){ echo \'disabled\'; } ?>\">__N__                                <a <?php if($page_no < $total_no_of_pages) { echo \"href=\'?p=$next_page\'\"; } ?>>__N__                                    <b><?php print $weblangs->trans(\"next\"); ?></b>__N__                                    <i class=\"icon-chevron-right\"></i>__N__                                </a>__N__                            </li>__N__              