r = new Societe($db);__N__        __N__        	if (!empty($customer_company)) {__N__        		$customer->name = $customer_company;__N__        	} else {__N__        		$customer->name = dolGetFirstLastname($customer_firstname, $customer_lastname);__N__        	}__N__        	__N__        	$customer->address = $customer_address;__N__        	$customer->country_id = $customer_country;__N__        __N__        	$customer->name_alias = \'\';__N__        	$customer->idprof2 = $customer_siret;__N__        	$customer->idprof3 = $customer_tin;__N__        	__N__        	// TODO if a valid siret set define customer as a company__N__        __N__        	$customer->client = \'2\';__N__        	$customer->code_client = \'auto\';__N__        __N__        	$customer->fournisseur = \'0\';__N__        	$customer->code_fournisseur = \'auto\';__N__        __N__        	//$customer->default_lang = $obj->dol_lang_code;__N__        	__N__        	$result = $customer->create($user);__N__        	if ($result < 0) {__N__    			$error++;__N__    			$error_messages[] = $weblangs->trans(\"errorOccurred\");__N__    		}__N__        	$rowid_soc = $result;__N__        __N__            if ($error == 0) {__N__    			// Add  groups__N__    			$ret_cat = $customer->setCategories($marketplace_third_parties_category, \'customer\');__N__    			if ($ret_cat < 0) {__N__    				$error++;__N__    				$error_messages[] = $weblangs->trans(\"errorOccurred\");__N__    			}__N__    __N__    __N__    			// Add default contact__N__    			$customer->name_bis = $customer_lastname;__N__    			$customer->email = $customer_email;__N__    			$customer->firstname = $customer_firstname;__N__    			$customer->address = $customer_address;__N__    			$customer->country_id = $customer_country;__N__    __N__    			$ret_contact = $customer->create_individual($user);__N__    			if ($ret_contact < 0) {__N__    				$error++;__N__    				$error_messages[] = $weblangs->trans(\"errorOccurred\");__N__    			} else {__N__    			    $c