rray_reverse($full_version_tree) as $key => $value) : ?>__N__        <?php if ($value[\'rowid\'] != $mvcid) : ?>__N__          <a class=\"tag_version\" href=\"index.php?tag=<?php print $value[\'label\']; ?>\" title=\"<?php print $value[\'description\']; ?>\"> <?php print $value[\'label\']; ?> </a>__N__        <?php endif ?>__N__      <?php endforeach ?>__N__    </div>__N__  </div>__N____N____N__  <div id=\"special_block_right\" class=\"block\">__N__    <p class=\"title_block\">__N__      <a href=\"#\" title=\"Specials\"> <?php print $weblangs->trans(\"specialPromo\"); ?> </a>__N__    </p>__N__    <div class=\"block_content products-block\">__N__      <ul>__N__        <?php__N__        $mcsid = getDolGlobalInt(\"MARKETPLACE_SPECIAL_CATEGORY_ID\");__N__        if (empty($mcsid)) {__N__          print $weblangs->trans(\"variableNotDefined\", \'MARKETPLACE_SPECIAL_CATEGORY_ID\');__N__          exit;__N__        }__N__        $root_cat_object = new Categorie($db);__N__        $result = $root_cat_object->fetch($mcsid);__N__        $limit = 2;__N__        __N__        $sql = \"SELECT c.fk_product as id, o.ref, o.ref_ext, ol.label, ol.description, o.datec, o.price_ttc \";__N__        $sql .= \"FROM llx_categorie_product as c, \";__N__        $sql .= \"llx_product as o, \";__N__        $sql .= \"llx_product_lang as ol \";__N__        $sql .= \"WHERE o.entity IN (1) AND c.fk_categorie = \".$root_cat_object->id.\" AND c.fk_product = o.rowid AND ol.fk_product = o.rowid \";__N__        $sql .= \"AND ol.lang = \'\".$db->escape($current_lang).\"\' AND o.tosell = 1 \";__N__        $sql .= $db->order(\'datec\', \'DESC\');__N__        $sql .= $db->plimit($limit);__N____N__        if ($resql = $db->query($sql)) {__N__		    $key = 0;__N__			while ($product = $resql->fetch_object()) {__N__			    $product->element = \"product\";__N__			    __N__			    $discount = marketplace_check_discount($product->id);__N__		        $discounted_price = marketplace_calculate_discounted_price($product->price_ttc, $discount);__N____N__                $thumb = getImagePublicURLOfObject($product, 1, \'_small\');__N__                ?>__N__                <li class=\"clearfix\">__N__                    <a class=\"products-block-image\" href=\"product.php?id=<?php print $product->id; ?>\">__N__                      <img class=\"replace-2x img-responsive width98\" src=\"<?php print $thumb; ?>\" alt=\"\" title=\"<?php print $product->label; ?>\" />__N__                    </a>__N__                    <div class=\"product-content\">__N__                      <h5>__N__                        <a class=\"product-name truncate2\" href=\"product.php?id=<?php print $product->id; ?>\" title=\"<?php print $product->label; ?>\"> <?php print $product->label; ?> </a>__N__                      </h5>__N__                      <p class=\"product-description truncate2\">__N__                        <?php__N__                        if (isset($product->description)) {__N__                          print htmlspecialchars(strip_tags($product->description), ENT_QUOTES, \'UTF-8\');__N__                        }__N__                        ?>__N__                      </p>__N__                      <div class=\"price-box\">__N__                            <?php if ($discounted_price != $product->price_ttc): ?>__N__                            	<span class=\"price special-price\"> <?php print price($discounted_price); ?> € </span>__N__                                <span class=\"old-price\"> <?php print price($product->price_ttc); ?> € </span>__N__                            <?php else: ?>__N__                                <span class=\"price\"> <?php print price($product->price_ttc); ?> € </span>__N__                            <?php endif ?>__N__                        <!--<span class=\"price-percent-reduction\">-50%</span>__N__        				<span class=\"old-price\"> 300,00 € </span>-->__N__                      </div>__N__                    </div>__N__                </li>__N__        <?php __N__			}__N__		}__N__        ?>__N__      </ul>__N__      <div>__N__        <a class=\"btn btn-default button button-small\" href=\"index.php?tag=Specials\" title=\"<?php print $weblangs->trans(\"allSpecials\"); ?>\">__N__          <span><?php print $weblangs->trans(\"allSpecials\"); ?> <i class=\"icon-chevron-right right\"></i>__N__          </span>__N__        </a>__N__      </div>__N__    </div>__N__  </div>__N____N__  <div id=\"new-products_block_right\" class=\"block products_block\" contenteditable=\"true\">__N__    <h4 class=\"title_block\">__N__      <a href=\"#\" title=\"New products\"><?php print $weblangs->trans(\"newProducts\"); ?></a>__N__    </h4>__N__    <div class=\"block_content products-block\">__N__      <ul class=\"products\">__N__        <?php__N__        $result = $root_cat_object->fetch($root_category_id, \"\");__N__        if ($result <= 0) {__N__          dol_print_error($db, $root_cat_object->error);__N__          exit;__N__        }__N__        $limit = 1;__N__        __N__        $sql = \"SELECT c.fk_product as id, o.ref, o.ref_ext, ol.label, ol.description, o.datec, o.price_ttc \";__N__        $sql .= \"FROM llx_categorie_product as c, \";__N__        $sql .= \"llx_product as o, \";__N__        $sql .= \"llx_product_lang as ol \";__N__        $sql .= \"WHERE o.entity IN (1) AND c.fk_categorie = \".$root_cat_object->id.\" AND c.fk_product = o.rowid AND ol.fk_product = o.rowid \";__N__        $sql .= \"AND ol.lang = \'\".$db->escape($current_lang).\"\' AND o.tosell = 1 \";__N__        $sql .= $db->order(\'datec\', \'DESC\');__N__        $sql .= $db->plimit($limit);__N____N__        if ($resql = $db->query($sql)) {__N__		    $key = 0;__N__			while ($product = $resql->fetch_object()) {__N__			    $product->element = \"product\";__N__			    __N__			    $discount = marketplace_check_discount($product->id);__N__		        $discounted_price = marketplace_calculate_discounted_price($product->price_ttc, $discount);__N____N__                $thumb = getImagePublicURLOfObject($product, 1, \'_small\');__N__                ?>__N__                <li class=\"clearfix\">__N__                    <a class=\"products-block-image\" href=\"product.php?id=<?php print $product->id; ?>\" title=\"<?php print $product->label; ?>\">__N__                      <img class=\"replace-2x img-responsive width98\" src=\"<?php print $thumb; ?>\" alt=\"\" title=\"<?php print $product->label; ?>\" />__N__                    </a>__N__                    <div class=\"product-content\">__N__                      <h5>__N__                        <a class=\"product-name truncate2\" href=\"product.php?id=<?php print $product->id; ?>\" title=\"<?php print $product->label; ?>\"> <?php print $product->label; ?> </a>__N__                      </h5>__N__                      <p class=\"product-description truncate2\">__N__                        <?php__N__                        if (isset($product->description)) {__N__                          print htmlspecialchars(strip_tags($product->description), ENT_QUOTES, \'UTF-8\');__N__                        }__N__                        ?>__N__                      </p>__N__                      <div class=\"price-box\">__N__        				<?php if ($discounted_price != $product->price_ttc): ?>__N__                        	<span class=\"price special-price\"> <?php print price($discounted_price); ?> € </span>__N__                            <span class=\"old-price\"> <?php print price($product->price_ttc); ?> € </span>__N__                        <?php else: ?>__N__                            <span class=\"price\"> <?php print price($product->price_ttc); ?> € </span>__N__                        <?php endif ?>__N__                      </div>__N__                    </div>__N__                </li>__N__        <?php __N__			} __N__        }__N__        ?>__N__      </ul>__N__      <div>__N__        <a href=\"index.php?cat=<?php print $root_category_id; ?>&list=new\" title=\"<?php print $weblangs->trans(\"allNewProducts\"); ?>\" class=\"btn btn-default button button-small\">__N__          <span><?php print $weblangs->trans(\"allNewProducts\"); ?> <i class=\"icon-chevron-right right\"></i>__N__          </span>__N__        </a>__N__      </div>__N__    </div>__N__  </div>__N__</div>__N__<!-- BEGIN LEFT SIDEBAR -->', '', 0);

