duct_id = GETPOST(\'product_id\', \'int\');__N__$product_thumb = GETPOST(\'product_thumb\');__N__$product_qty = GETPOST(\'product_quantity\', \'int\');__N____N__if($product_id && $product_qty) {__N____N__    /*foreach($_POST as $key => $value){__N__        $new_product[$key] = $value;__N__    }*/__N____N__	$object = new Product($db);__N__	$get_product = $object->fetch($product_id);__N____N__    $new_product[\'product_id\'] = $object->id;__N__    $new_product[\'product_thumb\'] = $product_thumb;__N__    $new_product[\'product_name\'] = $object->label;__N__    $new_product[\'product_ref\'] = $object->ref;__N__    $new_product[\'product_price\'] = $object->price_ttc;__N__    $new_product[\'product_qty\'] = $product_qty;__N____N__        if(isset($_SESSION[\'products\'])){  //if session var already exist__N____N__            if(isset($_SESSION[\'products\'][$new_product[\'product_id\']])) //check item exist in products array__N__            {__N__                unset($_SESSION[\'products\'][$new_product[\'product_id\']]); //unset old item__N__            }__N__        }__N____N__        $_SESSION[\'products\'][$new_product[\'product_id\']] = $new_product;	//update products with new item array__N____N__    $total_items = count($_SESSION[\'products\']); //count total items__N____N__    $popup_product_html = \'__N__        <span class=\"cross\" title=\"Close window\"></span>__N__        <h2>__N__            <i class=\"icon-ok\"></i>Product successfully added to your shopping cart__N__        </h2>__N__        <div class=\"product-image-container layer_cart_img\">__N__            <img class=\"layer_cart_img img-responsive\" src=\"\'.$product_thumb.\'\" alt=\"Subtotal V3\" title=\"\'.$object->label.\'\">__N__        </div>__N__        <div id=\"layer_cart_product_info\">__N__            <span id=\"layer_cart_product_title\" class=\"product-name\">\'.$object->label.\'</span>__N__            <span id=\"layer_cart_product_attributes\"></span>__N__            <div>__N__            <strong class=\"dark\">Quantity</strong>__N__            <span id=\"layer_cart_product_quantity\">\'.$product_qty.\'</span>__N__            </div>__N__            <div>__N__            <strong class=\"dark\">Total</strong>__N__            <span id=\"layer_cart_product_price\">(\'.($product_qty * $object->price_ttc).\' €)</span>__N__            </div>__N__        </div>\';__N____N__    die(json_encode(array(\'items\'=>$total_items,\'popup_product_html\'=>$popup_product_html))); //output json__N____N__}__N____N__if(isset($_POST[\'load_cart\']) && $_POST[\'load_cart\'] == 1)__N__{__N__    if(isset($_SESSION[\'products\']) && count($_SESSION[\'products\'])>0){ //if we have session variable__N__        $cart_box = \'\';__N__        $total = 0;__N__        foreach($_SESSION[\'products\'] as $cart_product){ //loop though items and prepare html content__N____N__            $cart_box .= \"<dl class=\\\"products\\\">__N__                <dt class=\\\"item\\\" data-id=\\\"cart_block_product_\" . $cart_product[\'product_id\'] . \"\\\" style=\\\"display: block;\\\">__N__                    <a class=\\\"cart-images\\\" href=\\\"product.php?id=\" . $cart_product[\'product_id\'] . \"\\\" title=\\\"\" . $cart_product[\'product_name\'] . \"\\\"><img src=\\\"\" . $cart_product[\'product_thumb\'] . \"\\\" alt=\\\"\" . $cart_product[\'product_name\'] . \"\\\"></a>__N__                    <div class=\\\"cart-info\\\">__N__                        <div class=\\\"product-name\\\"><span class=\\\"quantity-formated\\\"><span class=\\\"quantity\\\">\" . $cart_product[\'product_qty\'] . \"</span>&nbsp;x&nbsp;</span><a href=\\\"product.php?id=\" . $cart_product[\'product_id\'] . \"\\\" title=\\\"\" . $cart_product[\'product_name\'] . \"\\\" class=\\\"cart_block_product_name truncate3\\\">\" . $cart_product[\'product_name\'] . \"</a></div>__N__                        <span class=\\\"price\\\">\" . price($cart_product[\'product_price\']) . \" €</span>__N__                    </div>__N__                    <span class=\\\"remove_link\\\"><a rel=\\\"nofollow\\\" class=\\\"ajax_cart_block_remove_link\\\" href=\\\"\\\" data-code=\\\"\" . $cart_product[\'product_id\'] . \"\\\"> </a></span>__N__                </dt>__N__            </dl>\";__N____N__            $subtotal = ($cart_product[\'product_price\'] * $cart_product[\'product_qty\']);__N__            $total = ($total + $subtotal);__N__        }__N__        $cart_box .= \'__N__        <div class=\"cart-prices\">__N__            <div class=\"cart-prices-line first-line\">__N__                <span class=\"price cart_block_shipping_cost ajax_cart_shipping_cost\"> Free shipping! </span>__N__                <span> Shipping </span>__N__            </div>__N__            <div class=\"cart-prices-line last-line\">__N__                <span class=\"price cart_block_total ajax_block_cart_total\">\' . $total . \' €</span>__N__                <span>Total</span>__N__            </div>__N__            <p> Prices may vary depending on your country. </p>__N__        </div>__N__        <p class=\"cart-buttons\">__N__            <a id=\"button_order_cart\" class=\"btn btn-default button button-small\" href=\"\' . $order_page_url . \'\" title=\"Check out\" rel=\"nofollow\">__N__                <span> Check out <i class=\"icon-chevron-right right\"></i>__N__                </span>__N__            </a>__N__        </p>\';__N__        die($cart_box); //exit and output content__N__    }else{__N__        die(\"<p class=\'cart_block_no_products\'> No products </p>\"); //we have empty cart__N__    }__N__}__N____N__if(isset($_POST[\'load_popup_products_html\']) && $_POST[\'load_popup_products_html\'] == 1)__N__{__N__    if(isset($_SESSION[\'products\']) && count($_SESSION[\'products\'])>0){ //if we have session variable__N__        $total = 0;__N__        foreach($_SESSION[\'products\'] as $cart_product){ //loop though items and prepare html content__N__            $subtotal = ($cart_product[\'product_price\'] * $cart_product[\'product_qty\']);__N__            $total = ($total + $subtotal);__N__        }__N__        if(count($_SESSION[\'products\']) == 1){__N__            $title = \'<span class=\"ajax_cart_product_txt\"> There is 1 item in your cart. </span>\';__N__        }else{__N__            $title = \'<span class=\"ajax_cart_product_txt_s\"> There are <span class=\"ajax_cart_quantity\">\'.count($_SESSION[\'products\']).\'</span> items in your cart. </span>\';__N__        }__N__        $popup_products_html = \'__N__        <h2>\'.$title.\'</h2>__N__        <div class=\"layer_cart_row\">__N__            <strong class=\"dark\"> Total products (tax incl.) </strong>__N__            <span class=\"ajax_block_products_total\">\'.$total.\' €</span>__N__        </div>__N__        <div class=\"layer_cart_row\">__N__            <strong class=\"dark\"> Total shipping&nbsp;(tax incl.) </strong>__N__            <span class=\"ajax_cart_shipping_cost\"> Free shipping! </span>__N__        </div>__N__        <div class=\"layer_cart_row\">__N__            <strong class=\"dark\"> Total (tax incl.) </strong>__N__            <span class=\"ajax_block_cart_total\">\'.$total.\' €</span>__N__        </div>__N__        <div class=\"button-container\">__N__            <span class=\"continue btn btn-default button exclusive-medium\" title=\"Continue shopping\">__N__            <span>__N__                <i class=\"icon-chevron-left left\"></i>Continue shopping </span>__N__            </span>__N__            <a class=\"btn btn-default button button-medium\" href=\"\' . $order_page_url . \'\" title=\"Proceed to checkout\" rel=\"nofollow\">__N__            <span> Proceed to checkout <i class=\"icon-chevron-right right\"></i>__N__            </span>__N__            </a>__N__        </div>\';__N____N__        die($popup_products_html); //exit and output content__N__    }__N__}__N____N____N__//Remove item from shopping cart__N__if(isset($_GET[\'remove_code\']) && isset($_SESSION[\'products\']))__N__{__N__    $product_code   = $_GET[\'remove_code\']; //get the product code to remove__N____N__    if(isset($_SESSION[\'products\'][$product_code]))__N__    {__N__        unset($_SESSION[\'products\'][$product_code]);__N__    }__N____N__    $total_items = count($_SESSION[\'products\']);__N__    die(json_encode(array(\'items\'=>$total_items)));__N__}__N__', '', 0);

-- Page ID 56__+MAX_llx_website_page__ - Aliases _library_page_lib --;
INSERT INTO llx_website_page(rowid, fk_page, fk_website, pageurl, aliasalt, title, description, lang, image, keywords, status, date_creation, tms, import_key, grabbed_from, type_container, htmlheader, content, author_alias, allowed_in_frames) VALUES(56__+MAX_llx_website_page__, null, __WEBSITE_ID__, '_library_page_lib', '', 'lib', '', '', '', '', '1', '2024-05-22 11:22:17', '2024-09-06 20:04:20', null, '', 'library', '', '<?php__N__/**__N__ * Set session language based on:__N__ * - Cookie \'lang\' (if exists)__N__ * - Browser language (if not present among website languages, use website main language)__N__ * - Form field \'language\' (if present)__N__ *__N__ * The function also updates the product labels of the products in the session__N__ * to match the current language.__N__ *__N__ * @return bool__N__ */__N__function setSessionLang() {__N__   global $langs, $website, $weblangs, $db;__N__    // Get available langs for the website__N__    $website_langs = getAvailableLangs();__N__    $available_langs = $website_langs[\'langs\'];__N__    $langs_code_shortcode = $website_langs[\'langs_code_shortcode\'];__N____N__    // If no language is set or if a new language is selected (via form field)__N__    if (!isset($_COOKIE[\'lang\']) || GETPOST(\'language\', \'alpha\')) {__N__        // Get browser language__N__        $browser_lang = $weblangs->getDefaultLang(1);__N____N__        // Change used lang__N__        $new_lang = GETPOST(\'language\', \'alpha\');__N__        if ($new_lang && array_key_exists ($new_lang, $available_langs)) {__N__            setcookie(\'lang\', $new_lang, time() + (86400 * 30), \'/\');__N__            $session_lang = $new_lang;__N__        }__N____N__        // If no language is set in cookie and browser language is not present among available languages__N__        // use website main language as default__N__        if (!isset($_COOKIE[\'lang\'])) {__N__            if (array_key_exists ($browser_lang, $available_langs)){__N__                // The browser language is present among the available languages__N__                setcookie(\'lang\', $browser_lang, time() + (86400 * 30), \'/\');__N__                $session_lang = $browser_lang;__N__            } else {__N__                // The browser language is not present among the available languages__N__                setcookie(\'lang\', $website->lang, time() + (86400 * 30), \'/\');__N__                $session_lang = $website->lang;__N__            }__N__        }__N____N__        // Set session lang__N__        if (!empty($session_lang)) {__N__            $_SESSION[\'lang\'][\'shortcode\'] = $session_lang;__N__            $_SESSION[\'lang\'][\'code\'] = $langs_code_shortcode[$session_lang];__N____N__            // Update product labels in session to match current language__N__            if (isset($_SESSION[\'products\'])) {__N__                $langs->setDefaultLang($_SESSION[\'lang\'][\'code\']);__N__                // Loop over products in session and update label__N__                foreach ($_SESSION[\'products\'] as $key => $value) {__N__                    $object = new Product($db);__N__                    $get_product = $object->fetch($key);__N__                    $_SESSION[\'products\'][$key][\'product_name\'] = $object->label;__N__                }__N__            }__N__        }__N____N__    }__N____N__    // If session language is not set, use cookie language__N__    if (empty($_SESSION[\'lang\'][\'shortcode\']) || empty($_SESSION[\'lang\'][\'code\'])) {__N__        $_SESSION[\'lang\'][\'shortcode\'] = $_COOKIE[\'lang\'];__N__        $_SESSION[\'lang\'][\'code\'] = $langs_code_shortcode[$_COOKIE[\'lang\']];__N__    }__N____N__    // Set Dolibarr default language__N__    $langs->setDefaultLang($_SESSION[\'lang\'][\'code\']);__N__    $weblangs->setDefaultLang($_SESSION[\'lang\'][\'code\']);__N__    $langs->loadLangs(array(\"main\", \"website\"));__N__    $weblangs->loadLangs(array(\"main\", \"website\", \"marketplace@marketplace\"));__N____N__    return true;__N__}__N____N__/**__N__ * The function loops through the languages of the website configuration and__N__ * checks if they exist in the list of supported languages. If they do, they__N__ * are added to the $available_langs array, along with their corresponding__N__ * shortcode in the $langs_code_shortcode array.__N__ *__N__ * The function returns an array with two keys: \'langs\' containing the list__N__ * of available languages and \'langs_code_shortcode\' containing the list__N__ * of corresponding shortcodes.__N__ *__N__ * @return array__N__ */__N__function getAvailableLangs() {__N__    global $website, $weblangs;__N____N__    $available_langs = array();__N__    $langs_code_shortcode = array();__N__    $ws_langs_list = explode(\',\', $website->lang.\',\'.$website->otherlang);__N__    $dol_available_langs = $weblangs->get_available_languages(DOL_DOCUMENT_ROOT, 1, 2, 1);__N__    foreach ($ws_langs_list as $lang) {__N__        foreach ($dol_available_langs as $key => $value) {__N__            if (strpos($key, $lang) === 0) {__N__                $available_langs[$lang] = $value;__N__                $langs_code_shortcode[$lang] = $key;__N__                break;__N__            }__N__        }__N__    }__N__    __N__    return array(\'langs\' => $available_langs, \'langs_code_shortcode\' => $langs_code_shortcode);__N__}__N____N__function getOrganizedTree($id,$sort = \'position\')__N__{__N__    global $db;__N__    $root_cat_object = new Categorie($db);__N__    $result = $root_cat_object->fetch($id);__N__    $root_category_id = $root_cat_object->id;__N__    $root_category_type = $root_cat_object->type;__N__    $cats = $root_cat_object->get_filles();__N__    if (count($cats) < 1) {__N__        return array();__N__    } else {__N__        $categstatic = new Categorie($db);__N__        $fulltree = $categstatic->get_full_arbo($root_category_type, $root_category_id, 1);__N__        $organized_tree = buildTree($fulltree, $root_category_id);__N__        usort($organized_tree, build_sorter($sort));__N__        return $organized_tree;__N__    }__N__}__N____N__function buildTree(array &$elements, $parentId = 0)__N__{__N__  $branch = array();__N__  foreach ($elements as $element) {__N__    if ($element[\'fk_parent\'] == $parentId) {__N__      $children = buildTree($elements, $element[\'id\']);__N__      if ($children) {__N__        $element[\'children\'] = $children;__N__      }__N__      $branch[$element[\'rowid\']] = $element;__N__    }__N__  }__N__  return $branch;__N__}__N____N__function build_sorter($key)__N__{__N__  return function ($a, $b) use ($key) {__N__    return strnatcmp($a[$key], $b[$key]);__N__  };__N__}__N____N____N__function ws_login($customer_email, $customer_passwd) {__N__    global $db, $website;__N____N__    $sql  = \"select lsa.rowid, lsa.pass_encoding, lsa.pass_crypted, ls.nom, fk_soc, lsa.login \";__N__    $sql .= \"from \".MAIN_DB_PREFIX.\"societe_account lsa \";__N__    $sql .= \"left join \".MAIN_DB_PREFIX.\"societe ls on ls.rowid = lsa.fk_soc \";__N__    $sql .= \"where lsa.login = \'\" . $db->escape($customer_email) . \"\' \";__N__    $sql .= \"and lsa.fk_website = \" . (int) $website->id . \" \";__N__    $sql .= \"limit 1\";__N____N__    $result = $db->query($sql);__N__    $objsql = $db->fetch_object($result);__N____N__    $is_logged = 0;__N__    $check_accaount = 0;__N__    $check_password = 0;__N__    $is_preferred_member = 0;__N__    if (!empty($objsql)) {__N__        $check_accaount = 1;__N__        if ($objsql->pass_encoding == \'dolibarr\') {__N__            $check_password = dol_verifyHash ($customer_passwd, $objsql->pass_crypted);__N__        }__N__        if ($objsql->pass_encoding == \'dolistore\') {__N__            $dolistore_key = getDolGlobalString(\"MARKETPLACE_SECRET_KEY\");__N__            $to_check = md5($dolistore_key.$customer_passwd);__N__            if ($objsql->pass_crypted === $to_check){__N__                $check_password = 1;__N__            }__N__        }__N__        $is_logged = $check_accaount && $check_password;__N__    }__N__    __N__    if ($is_logged > 0) {__N__        $preferredCustomersCatId = getDolGlobalInt(\"MARKETPLACE_PROSPECTCUSTOMER_PREFERRED_ID\");__N__        if (!empty($preferredCustomersCatId)) {__N__            $category = new Categorie($db);__N__            $user_categories = $category->getListForItem($objsql->fk_soc);__N____N__            foreach ($user_categories as $user_categorie) {__N__                if (!empty($user_categorie[\'id\']) && $user_categorie[\'id\'] == $preferredCustomersCatId) {__N__                    $is_preferred_member = 1;__N__                    break;__N__                }__N__            }__N__        }__N__    }__N____N__    return array(__N__        \'status\' => $is_logged,__N__        \'userid\' => $objsql->rowid,__N__        \'pseudo\' => $objsql->nom,__N__        \'email\' => $objsql->login,__N__        \'socid\' => $objsql->fk_soc,__N__        \'is_preferred_member\' => $is_preferred_member__N__    );__N__}__N____N____N__function ws_refreshCart($fk_soc) {__N__    global $db;__N__    // Find last validated, unpaid order of this user__N__    $sql_find_order = \"SELECT c.rowid, c.fk_statut, c.fk_soc, c.date_creation, c.module_source \";__N__    $sql_find_order .= \"FROM \".MAIN_DB_PREFIX.\"commande as c \";__N__    $sql_find_order .= \"WHERE c.fk_soc = \" . $fk_soc . \" and c.fk_statut= 1 and c.facture = 0 and c.module_source = \'Marketplace\' \";__N__    $sql_find_order .= \"order by c.date_creation desc \";__N__    $sql_find_order .= \"limit 1\";__N____N__    $find_order = $db->query($sql_find_order);__N__    $obj_order = $db->fetch_object($find_order);__N____N__    // If there is an order, replace the current cart articles (fetched from SESSION) with the last validated, unpaid order.__N__    if (!empty($obj_order)) {__N__        $order_id = $obj_order->rowid;__N__        require_once DOL_DOCUMENT_ROOT.\"/commande/class/commande.class.php\";__N__        $commande = new Commande($db);__N__        $ret = $commande->fetch($order_id);__N____N__        $commande->fetch_lines();__N__        $lines = $commande->lines;__N____N__        foreach ($lines as $line){__N__            $product = new Product($db);__N__            $ret = $product->fetch($line->fk_product);__N__            __N__            // check discount and calculate discounted price__N__		    $discount = marketplace_check_discount($product->id);__N__		    $discounted_price = marketplace_calculate_discounted_price($product->price_ttc, $discount);__N__		    __N__		    if ($discounted_price != $product->price_ttc) {__N__		        $price_to_display = $discounted_price;__N__		    } else {__N__		        $price_to_display = $product->price_ttc;__N__		    }__N__            __N__            $session_product[\'product_id\'] = $product->id;__N__            $session_product[\'product_thumb\'] = getImagePublicURLOfObject($product,1);__N__            $session_product[\'product_name\'] = $product->label;__N__            $session_product[\'product_ref\'] = $product->ref;__N__            $session_product[\'product_price\'] = $price_to_display;__N__            $session_product[\'product_qty\'] = $line->qty;__N____N__            if(isset($_SESSION[\'products\'])){__N__                if(isset($_SESSION[\'products\'][$session_product[\'product_id\']]))__N__                {__N__                    unset($_SESSION[\'products\'][$session_product[\'product_id\']]);__N__                }__N__            }__N__            $_SESSION[\'products\'][$session_product[\'product_id\']] = $session_product;__N__        }__N__    }__N____N__    return 1;__N__}__N____N__/**__N__ * Check the validity of a module (rules must match the one in htdocs/admin/modules__N__ *__N__ * @return  array   Array of results__N__ */__N__function checkZipFile()__N__{__N__	global $weblangs;__N____N__	$error=0;__N__	$return = array(__N__		\'product_file_path\' => null,__N__		\'upload\' => 1,__N__		\'errormsg\' => null__N__	);__N____N__	$originalfilename=$_FILES[\'virtual_product_file\'][\'name\'];__N____N__	if (strlen($originalfilename) > 48)__N__	{__N__		$error++;__N__		$return[\'errormsg\'] = $weblangs->trans(\"fileNameLengthError\");__N__		///$return[\'errormsg\'] = $this->l(\'Name of file must not be longer than 48 characters,  including the prefix (\"module_\", \"theme_\" or \"website_\"), the version suffix and the extension (\".zip\" for example) !\');__N__	}__N____N__	dol_syslog(\"Upload or reupload file \" . $originalfilename);__N____N__	if ($_FILES[\'virtual_product_file\'][\'error\'])__N__	{__N__		switch ($_FILES[\'virtual_product_file\'][\'error\'])__N__		{__N__			case 1: // UPLOAD_ERR_INI_SIZE__N__				$return[\'errormsg\'] = $weblangs->trans(\"fileSizeHigherServerLimit\");__N__				///$return[\'errormsg\'] = $this->l(\'File size is higher than server limit !\');__N__				break;__N__			case 2: // UPLOAD_ERR_FORM_SIZE__N__				$return[\'errormsg\'] = $weblangs->trans(\"fileSizeHigherHtmlFormLimit\");__N__				///$return[\'errormsg\'] = $this->l(\'File size if higher than limit in HTML form !\');__N__				break;__N__			case 3: // UPLOAD_ERR_PARTIAL__N__				$return[\'errormsg\'] = $weblangs->trans(\"uploadAborted\");__N__				///$return[\'errormsg\'] = $this->l(\'File transfert was aborted !\');__N__				break;__N__			case 4: // UPLOAD_ERR_NO_FILE__N__				$return[\'errormsg\'] = $weblangs->trans(\"noFileOrSizeZero\");__N__				///$return[\'errormsg\'] = $this->l(\'File name was not defined or file size is null !\');__N__				break;__N__		}__N__		$return[\'upload\'] = -1;__N__		$error++;__N__	}__N____N__	if (! $error && ! preg_match(\'/(\\.apk|\\.odt|\\.pdf|\\.svg|\\.zip|\\.txt)$/i\',$originalfilename))__N__	{__N__		$return[\'errormsg\'] = $weblangs->trans(\"packageRuleViolation\") . \'<br>\';__N__		$return[\'errormsg\'].= $weblangs->trans(\"packageFileNameExtension\");__N__		///$return[\'errormsg\'] = $this->l(\'Package seems to not respect some rules:\').\'<br>\';__N__		///$return[\'errormsg\'].= $this->l(\'Package file name must end with extension .odt, .pdf, .svg, .zip, .txt or .apk\');__N__		$return[\'upload\'] = -1;__N__		$error++;__N__	}__N____N__	if (! $error && preg_match(\'/(\\.txt)$/i\',$originalfilename) && ! preg_match(\'/(README)\\.txt$/i\',$originalfilename))__N__	{__N__		$return[\'errormsg\'] = $weblangs->trans(\"packageRuleViolation\") . \'<br>\';__N__		$return[\'errormsg\'].= $weblangs->trans(\"packageReadmeTxt\");__N__		$return[\'upload\'] = -1;__N__		$error++;__N__	}__N____N__	if (! $error && preg_match(\'/(\\.zip)$/i\',$originalfilename))__N__	{__N__		if (! preg_match(\'/^(module[a-zA-Z0-9]*|theme|website)_/\', $originalfilename))__N__		{__N__			$return[\'errormsg\'] = $weblangs->trans(\"packageRuleViolation\") . \'<br>\';__N__			$return[\'errormsg\'].= $weblangs->trans(\"packageFileNamePrefix\") . \'<br>\';__N__			$return[\'errormsg\'].= $weblangs->trans(\"packageBuildTool\");__N__			$return[\'upload\'] = -1;__N__			$error++;__N__		}__N____N__		if (! preg_match(\'/\\-([0-9][0-9\\.]*)\\.zip$/i\',$originalfilename))__N__		{__N__			$return[\'errormsg\'] = $weblangs->trans(\"packageRuleViolation\") . \'<br>\';__N__			$return[\'errormsg\'].= $weblangs->trans(\"packageFileNameVersion\") . \'<br>\';__N__			$return[\'errormsg\'].= $weblangs->trans(\"packageBuildTool\");__N__			$return[\'upload\'] = -1;__N__			$error++;__N__		}__N____N__		// Example of allowed values:__N__		// module_mymodule-x.y.zip, moduleprestashop_mymodule-x.y.zip__N__		// theme_mytheme-x.y.zip__N__		// website_mytemplate-123456.zip__N__		if (! preg_match(\'/^module([a-zA-Z0-9]*)_([-a-zA-Z0-9_]+)\\-([0-9][0-9\\.]*)\\.zip$/i\',$originalfilename)__N__			&& ! preg_match(\'/^theme_([a-zA-Z0-9_]+)\\-([0-9][0-9\\.]*)\\.zip$/i\',$originalfilename)__N__			&& ! preg_match(\'/^website_([a-zA-Z0-9_]+)\\-([0-9][0-9\\.]*)\\.zip$/i\',$originalfilename)__N__			)__N__		{__N__			$return[\'errormsg\'] = $weblangs->trans(\"packageRuleViolation\") . \'<br>\';__N__			$return[\'errormsg\'].= $weblangs->trans(\"packageFileNameRules\") . \'<br>\';__N__			$return[\'errormsg\'].= $weblangs->trans(\"packageBuildTool\");__N__			$return[\'upload\'] = -1;__N__			$error++;__N__		}__N__	}__N____N__	if (! $error && preg_match(\'/(\\.zip)$/i\',$originalfilename))__N__	{__N__		$zip = new ZipArchive();__N__		$res = $zip->open($_FILES[\'virtual_product_file\'][\'tmp_name\']);__N__		if ($res === TRUE)__N__		{__N__			$resarray = validateZipFile($zip,$originalfilename,$_FILES[\'virtual_product_file\'][\'tmp_name\']);__N__			//$zip->close();        // already close by validateZipFile__N__			$error=$resarray[\'error\'];__N__			$return[\'upload\']=$resarray[\'upload\'];__N__			$return[\'errormsg\'] = $resarray[\'errormsg\'];__N__		}__N__		else__N__		{__N__			$return[\'errormsg\'] = $weblangs->trans(\"fileNotAnalyzed\") . \'<br>\';__N__			$return[\'errormsg\'].= $weblangs->trans(\"emailIfError\");__N__			$return[\'upload\'] = -1;__N__			$error++;__N__		}__N__	}__N____N__	if (! $error)__N__	{__N__		$return[\'errormsg\'] = \'\';__N__		$return[\'upload\'] = 1;__N__	}__N____N__	return $return;__N__}__N____N__/**__N__ * Check that a zip file is Dolibarr rule compliant__N__ */__N__function validateZipFile(&$zip,$originalfilename,$zipfile)__N__{__N__	global $count, $results;__N____N__	$error=0;__N__	$return = array(__N__		\'error\' => 0,__N__		\'errormsg\'=> null,__N__		\'upload\' => 0__N__	);__N____N__	dol_syslog(\"Validate zip file \" . $originalfilename);__N__	$subdir=basename($zipfile);__N__	//$dir=\'/home/dolibarr/dolistore.com/tmp/\'.$subdir;__N__	$dir=sys_get_temp_dir().\'/unzip-dir-\'.$subdir;__N__	mkdir($dir, 0777, true);__N__	$zip->extractTo($dir.\'/\');__N__	$zip->close();__N____N__	// Zip content of a module should be ./mymodule or ./htdocs/mymodule__N____N__	// But we first we check if we need to change $dir (for zip that are ./module/htdocs/module instead of ./htdocs/module)__N__	if (! $error && $dh = opendir($dir))__N__	{__N__		$nbofsubdirs=0;__N__		while (($file = readdir($dh)) !== false)__N__		{__N__			if ($file == \'.\' || $file == \'..\') continue;__N__			dol_syslog(\"We check if dir \".$dir.\'/\'.$file.\'/htdocs exists\');__N__			if (is_dir($dir.\'/\'.$file.\'/htdocs\'))__N__			{__N__				dol_syslog(\'Dir \'.$dir.\'/\'.$file.\'/htdocs exists. So we use dir=\'.$dir.\'/\'.$file.\' as root for package to analyse.\');__N__				$dir=$dir.\'/\'.$file;__N__				break;__N__			}__N__		}__N__		closedir($dh);__N__	}__N____N__	// Now $dir contains root of zip, so mymodule or htdocs/mymodule__N__	// Analyze files__N__	$ismodule=$istheme=0;__N____N__	if (preg_match(\'/^module([a-zA-Z0-9]*)_([-a-zA-Z0-9]+)\\-([0-9][0-9\\.]*)\\.zip$/i\',$originalfilename,$reg))__N__	{__N__		$ismodule=$reg[2];__N__		$extmoduleornot=$reg[1];__N__		if ($extmoduleornot) $ismodule=0;__N__	}__N__	if (preg_match(\'/^theme_([-a-zA-Z0-9]+)\\-([0-9][0-9\\.]*)\\.zip$/i\',$originalfilename,$reg)) $istheme=$reg[1];__N____N____N__	dol_syslog(\"Now dir is the directory with root of the zip = \".$dir, LOG_DEBUG);__N____N__	$dirmoduletheme = $dir.\'/\'.($ismodule?$ismodule:($istheme?$istheme:\'\'));__N__	if (is_dir($dir.\'/htdocs\')) $dirmoduletheme = $dir.\'/htdocs/\'.($ismodule?$ismodule:($istheme?$istheme:\'\'));__N__	$dirmodulethemeroot = dirname($dirmoduletheme);__N__	dol_syslog(\"Now dirmodulethemeroot = dir where is the module dir = \".$dirmodulethemeroot.\" and dirmoduletheme = dir with name of the module = \".$dirmoduletheme, LOG_DEBUG);__N____N__	if (! empty($ismodule) || ! empty($istheme))__N__	{__N__		dol_syslog(\"file ismodule=\".$ismodule.\" istheme=\".$istheme);__N__		// It\'s a module or theme file__N__		if (! $error && (! empty($ismodule) || ! empty($istheme)) && $dh = opendir($dir))__N__		{__N__			$nbofsubdirs=0; $direrror=\'\';__N__			while (($file = readdir($dh)) !== false)__N__			{__N__				if ($file == \'.\' || $file == \'..\' || $file == \'README\' || $file == \'README.txt\' || $file == \'README.md\') continue;__N__				dol_syslog(\"subdirs found for package:\".$file);__N__				$nbofsubdirs++;__N__				$alloweddirs=array(\'htdocs\', \'docs\', \'scripts\', \'test\', \'build\', ($ismodule?$ismodule:($istheme?$istheme:\'\')));__N__				if (! in_array($file,$alloweddirs))__N__				{__N__					$error++;__N__					$direrror=$file;__N__					break;__N__				}__N__			}__N__			if ($error)__N__			{__N__				$return[\'errormsg\'].= $weblangs->trans(\"validationFail\") . \'<br>\';__N__				$return[\'errormsg\'].= $weblangs->trans(\"moduleContents\") . \'<br>\';__N__				$return[\'errormsg\'].= $weblangs->trans(\"moduleDirectoryRule1\") . \'<br>\';__N__				$return[\'errormsg\'].= $weblangs->trans(\"moduleDirectoryRule2\") . \'<br>\';__N__				$return[\'errormsg\'].= $weblangs->trans(\"directoryFound\").\' \'.$direrror.\'<br><br>\'.\"\\n\";__N__			}__N__			closedir($dh);__N__		}__N____N__		// It\'s a module or theme file (check htdocs directory)__N__		if (! $error && ! empty($ismodule) && is_dir($dirmodulethemeroot) && $dh = opendir($dirmodulethemeroot))__N__		{__N__			dol_syslog(\"we scan module dir \".$dirmodulethemeroot.\" to be sure there is only one directory (with name of your module) into root\");__N__			$nbofsubdir=0; $lastdirfound=\'\';__N__			dol_syslog(\"check there is only one dir into root\");__N__			while (($file = readdir($dh)) !== false)__N__			{__N__				if ($file == \'.\' || $file == \'..\' || $file == \'README\' || $file == \'README.txt\' || $file == \'README.md\') continue;__N__				$lastdirfound = $file;__N__				dol_syslog(\"we found file or dir \".$file);__N__				$nbofsubdir++;__N__			}__N__			closedir($dh);__N__			if ($nbofsubdir >= 2 && ! is_file($dirmoduletheme.\'/metapackage.conf\'))__N__			{__N__				$return[\'errormsg\'].= $weblangs->trans(\"rootDirWarning\", $nbofsubdir, basename($dirmoduletheme)) . \'<br><br>\';__N__				$error++;__N__			}__N____N__			if ($ismodule != $lastdirfound)__N__			{__N__				if (is_file($dirmoduletheme.\'/metapackage.conf\'))__N__				{__N__					// TODO Check each dir found is inside list of modules__N____N__				} else {__N__					$return[\'errormsg\'].= $weblangs->trans(\"moduleNameMismatch\", $lastdirfound, $ismodule) .\'<br><br>\';__N__					$error++;__N__				}__N__			}__N__		}__N__		// Check \"custom\" compatibility__N__		if (! $error && ! empty($ismodule))__N__		{__N__			dol_syslog(\"check the good practice of code\");__N__			$count = 0;__N__			$results = array();__N____N__			$search = array(__N__				0 => array(__N__					\'name\'          => \'main\',__N__					\'types\'         => array(\'php\'),__N__					\'pattern\'       => \'(require|include).*(main|master)\\.inc\\.php\',__N__					\'multiple\'      => true     // Means we must find 0 or several times the pattern. Error if found 1 occurrence.__N__				),__N__				/*2 => array(__N__				 \'name\'          => \'dol_include_once\',__N__				 \'types\'         => array(\'php\', \'class.php\', \'lib.php\', \'modules.php\'),__N__				 \'pattern\'       => \'dol\\_include\\_once\\([\\\"\\\']\\/([^\\/]*)\\/.*;\',__N__				 \'contain\'       => array($ismodule)     // error if it contains__N__				 ),*/__N__				1 => array(__N__					\'name\'                  => \'dol_document_root\',__N__					\'types\'                 => array(\'php\', \'class.php\', \'lib.php\', \'modules.php\'),__N__					\'pattern\'               => \'(require|include)(_once)?\\(?(.*)[\\\"\\\']+\\)?;\',__N__					\'id\'                    => 3,           // eg. Use $regs[3] for test instead $regs[1]__N__					\'contain\'               => array(\'DOL_DOCUMENT_ROOT\'),__N__					\'notcontain\'            => array($ismodule),__N__					\'strict\'                => true         // if true (\'contain\' && \'notcontain\'), if false or not use (\'contain\' || \'notcontain\')__N__				)__N__			);__N____N__			getDirContents($dir, $search, $results);     // This include a global $count__N____N__			dol_syslog(\"count of errors = \".$count);__N____N__			if (!empty($count)) // count of errors is not null__N__			{__N__				foreach($results as $result)__N__				{__N__					if ($result[\'testname\'] == \'main\')__N__					{__N__						$return[\'errormsg\'].= $weblangs->trans(\"mainIncludeError\", $result[\'filename\'], $ismodule) .\'<br><br>\';__N__					}__N__					else if ($result[\'testname\'] == \'dol_include_once\')__N__					{__N__						$return[\'errormsg\'].= $weblangs->trans(\"dolIncludeError\", $result[\'filename\'], $ismodule) .\'<br>\';__N__						$return[\'errormsg\'].= $result[\'line\'].\'<br><br>\';__N__					}__N__					else if ($result[\'testname\'] == \'dol_document_root\')__N__					{__N__						$return[\'errormsg\'].= $weblangs->trans(\"docRootError\", $result[\'filename\'], $ismodule) .\'<br>\';__N__						$return[\'errormsg\'].= $result[\'line\'].\'<br><br>\';__N__					}__N__				}__N____N__				dol_syslog(\"file \".$originalfilename.\" is not compatible with custom directory!\", LOG_ERR);__N____N__				$error++;__N__			}__N__		}__N__	}__N__	else__N__	{__N__		dol_syslog(\"file \".$originalfilename.\" is not a module and not a theme!\", LOG_WARNING);    // It can be a doc, android app, ...__N__	}__N____N__	if (!empty($error))__N__	{__N__		dol_syslog(\"validateZipFile Error\");__N____N__		$link = \'<a target=\"_blank\" class=\"linktowiki\" href=\"https://wiki.dolibarr.org/index.php/Modules - Packaging rules and Dolistore validation rules\">Dolibarr wiki developer documentation</a>\';__N__		$return[\'errormsg\'].= $weblangs->trans(\"invalidZipFile\") .\'<br>\';__N__		$return[\'errormsg\'].= $weblangs->trans(\"seeDocumentation\", $link).\'<br>\';__N__		$return[\'errormsg\'].= \"<br>\\n\";__N__		$return[\'errormsg\'].= $weblangs->trans(\"contactDolistore\");__N__		$return[\'upload\'] = -1;__N__		$error++;__N__	}__N__	else__N__	{__N__		dol_syslog(\"validateZipFile OK\");__N__	}__N____N__	$return[\'error\'] = $error;__N____N__	return $return;__N__}__N____N__function getDirContents($dir, $search = array(), &$results = array())__N__{__N__	global $count;__N____N__	$files = scandir($dir);__N____N__	foreach ($files as $key => $value)__N__	{__N__		$path = realpath($dir . DIRECTORY_SEPARATOR . $value);__N__		if (!is_dir($path))__N__		{__N__			$content = file_get_contents($path);__N__			$fileName = basename($path);__N__			$fileNameWithoutTmp = preg_replace(\'/\\/tmp\\/unzip[^\\/]+\\//\', \'\', $path);__N____N__			$file_array = explode(\".\", $fileName);__N____N__			foreach ($search as $pattern)__N__			{__N__				if (count($file_array) > 1)__N__				{__N__					$ext = $file_array[1];__N__					if (!empty($file_array[2])) {__N__						$ext = $file_array[1] . \'.\' . $file_array[2];__N__					}__N____N__					if (in_array($ext, $pattern[\'types\']))__N__					{__N____N__						if (strpos($content, \'<?php\') !== 0) continue;  // We discard files that are not php pages (we discard scripts)__N__						if (strpos($path, \'htdocs_\') > 0) continue;  // We discard files that are files into htdocs_... because it is files for core, so no need to be compatible with custom__N__						if (strpos($path, \'phpunit\') > 0) continue;  // We discard files that are files into phpunit because it is files for core, so no need to be compatible with custom__N____N__						$regs = array();__N__						if (!empty($pattern[\'multiple\']))__N__						{__N__							preg_match_all(\'/\' . $pattern[\'pattern\'] . \'/\', $content, $regs);__N__							// Error if only one result (0 is ok, >1 is ok)__N__							if (!empty($regs) && count($regs[0]) == 1)__N__							{__N__								$results[] = array(__N__									\'testname\'      => $pattern[\'name\'],__N__									\'filename\'      => $fileNameWithoutTmp__N__								);__N__								$count++;__N__							}__N__						}__N__						else__N__						{__N__							$id = (!empty($pattern[\'id\']) ? $pattern[\'id\'] : 1);__N__							preg_match_all(\'/\' . $pattern[\'pattern\'] . \'/\', $content, $regs);__N____N__							if (!empty($regs) && !empty($regs[$id]))__N__							{__N__								foreach($regs[$id] as $i => $string)__N__								{__N__									if (!empty($pattern[\'contain\']) && is_array($pattern[\'contain\']))__N__									{__N__										foreach ($pattern[\'contain\'] as $contain)__N__										{__N__											// Mode strict true : doit contenir && ne pas contenir__N__											if (!empty($pattern[\'notcontain\']) && !empty($pattern[\'strict\']) && is_array($pattern[\'notcontain\']))__N__											{__N__												foreach ($pattern[\'notcontain\'] as $notcontain)__N__												{__N__													if (strstr($string, $contain) && strstr($string, $notcontain))__N__													{__N__														$results[] = array(__N__															\'testname\'      => $pattern[\'name\'],__N__															\'filename\'      => $fileName,__N__															\'line\'          => $regs[0][$i]__N__														);__N__														$count++;__N__													}__N__												}__N__											}__N__											// Mode strict false : doit contenir. Note strstr return false if not found__N__											else if (!strstr($string, $contain))        // If found__N__											{__N__												// We found $contain into $string__N__												$results[] = array(__N__													\'testname\'      => $pattern[\'name\'],__N__													\'filename\'      => $fileName,__N__													\'line\'          => $regs[0][$i]__N__												);__N__												$count++;__N__											}__N__										}__N__									}__N__									// Ou ne doit pas contenir__N__									if (empty($count) && !empty($pattern[\'notcontain\']) && empty($pattern[\'strict\']) && is_array($pattern[\'notcontain\']))__N__									{__N__										foreach ($pattern[\'notcontain\'] as $notcontain)__N__										{__N__											if (strstr($string, $notcontain))__N__											{__N__												$results[] = array(__N__													\'testname\'      => $pattern[\'name\'],__N__													\'filename\'      => $fileName,__N__													\'line\'          => $regs[0][$i]__N__												);__N__												$count++;__N__											}__N__										}__N__									}__N__								}__N__							}__N__						}__N__					}__N__				}__N__			}__N____N__		}__N__		else if ($value != \".\" && $value != \"..\") {__N__			getDirContents($path, $search, $results);__N__		}__N__	}__N__}__N____N__function checkImage($image)__N__{__N__	global $weblangs;__N__	$flagError = false;__N____N__	if ($image[\'error\'])__N__	{__N__		switch ($image[\'error\'])__N__		{__N__			case 1: // UPLOAD_ERR_INI_SIZE__N__				$flagError = $weblangs->trans(\"fileSizeHigherServerLimit\");__N__				break;__N__			case 2: // UPLOAD_ERR_FORM_SIZE__N__				$flagError = $weblangs->trans(\"fileSizeHigherHtmlFormLimit\");__N__				break;__N__			case 3: // UPLOAD_ERR_PARTIAL__N__				$flagError = $weblangs->trans(\"uploadAborted\");__N__				break;__N__			case 4: // UPLOAD_ERR_NO_FILE__N__				$flagError = $weblangs->trans(\"noFileOrSizeZero\");__N__				break;__N__		}__N__	}__N__	else__N__	{__N__		//Check exctention__N__		preg_match(\'/\\.([a-zA-Z0-9]+)$/\', $image[\'name\'], $reg);__N__		$extention_image = strtolower($reg[1]);__N__		if (! in_array($extention_image, array(\"jpg\",\"png\",\"gif\",\"jpeg\"))) {__N__			$flagError = $weblangs->trans(\"errorImageFormat\", strtoupper($extention_image));__N__		}__N__	}__N____N__	return $flagError;__N__}__N__/*__N__* @param array 		$product 		Selected value__N__* @param int	 	$id 			Id of the product__N__*/__N__function addProduct($object, $id = null) {__N____N__	global $db, $user, $conf, $weblangs;__N__	$error = 0;__N__    $error_messages = array();__N____N__	include_once DOL_DOCUMENT_ROOT . \'/product/class/product.class.php\';__N__	include_once DOL_DOCUMENT_ROOT . \'/categories/class/categorie.class.php\';__N__	include_once DOL_DOCUMENT_ROOT . \'/ecm/class/ecmfiles.class.php\';__N__	include_once DOL_DOCUMENT_ROOT . \'/core/lib/security.lib.php\';__N__	require_once DOL_DOCUMENT_ROOT . \'/core/class/html.formbarcode.class.php\';__N__	require_once DOL_DOCUMENT_ROOT . \'/fourn/class/fournisseur.product.class.php\';__N__	include_once DOL_DOCUMENT_ROOT .\'/societe/class/societeaccount.class.php\';__N____N____N__	// Load user and its permissions__N__	$result = $user->fetch(\'\', \'admin\'); // Load user for login \'admin\'. Comment line to run as anonymous user.__N__	if (!($result > 0)) {__N__		dol_print_error(null, $user->error);__N__		exit;__N__	}__N__	$user->getrights();__N____N__	// Get Account ID.__N__	$user_id = $_SESSION[\'user\'][\'id_user\'];__N__	$supplier_account = new SocieteAccount($db);__N__	$result = $supplier_account->fetch($user_id);__N__	$supplier_id = $supplier_account->fk_soc;__N____N__	// Get customer ID.__N__	$supplier = new Societe($db);__N__	$supplier->fetch($supplier_id);__N____N__	// Update customer to vendor__N__	$supplier->fournisseur = \'1\';__N__	$supplier->code_fournisseur = \'auto\';__N__	$supplier->update($supplier_id, $user);__N__	$result = $supplier->fetch($supplier_id);__N__	__N__	// We enable recursive category assignement (we use it for search and filter purposes)__N__    $conf->global->CATEGORIE_RECURSIV_ADD = 1;__N____N__	// Start of transaction__N__	$db->begin();__N____N__	//add one product__N__	$product = new Product($db);__N____N__	if ($id > 0) {__N__		$result = $product->fetch($id);__N__	} else {__N__		$dateRef = date (\"YmdHis\");__N__		$reference = \'c\'.$supplier->id.\'d\'.$dateRef;__N__		$product->ref = $reference;__N__		$product->barcode = \'auto\';__N__	}__N____N__	$product->type = 0;__N__	$product->status = $object[\'status\'];__N__	$product->status = 0;__N__	$product->status_buy = 0;__N____N__	$product->label = $object[\'name\'];__N__	$product->description = $object[\'short_description\'];__N__	$product->other = $object[\'description\'];__N__	$product->ref_ext = \'Marketplace\';__N__	$product->price = price2num($object[\'sale_price\']);__N__	$product->price_base_type = \'HT\';__N__	$product->tva_tx = \"20\";__N__	$product->mandatory_period = !empty(GETPOST(\"mandatoryperiod\", \'alpha\')) ? 1 : 0;__N____N__	// Extrafields__N__	$product->array_options[\'options_marketplace_module_keywords\'] = $object[\'keywords\'];__N__	$product->array_options[\'options_marketplace_module_version\'] = $object[\'version\'];__N__	$product->array_options[\'options_marketplace_min_version\'] = $object[\'version_min\'];__N__	$product->array_options[\'options_marketplace_max_version\'] = $object[\'version_max\'];__N__	$product->array_options[\'options_marketplace_contact_support\'] = $object[\'contact_support\'];__N__	$object[\'dolibarr_core_include\'] = $object[\'dolibarr_core_include\'] ? 1 : 0;__N__	$product->array_options[\'options_marketplace_allow_source_in_core\'] = $object[\'dolibarr_core_include\'];__N__	//$product->array_options[\'options_marketplace_short_description\'] = $object[\'resume\'];__N__	//$product->array_options[\'options_marketplace_reason_disabled\'] = $obj->dolibarr_disable_info;__N____N__	if ($id > 0) {__N__		$result = $product->update($id, $user);__N__	} else {__N__		$result = $product->create($user);__N__	}__N__	//$result = $product->create($user);__N____N__	if ($result < 0) {__N__		dol_syslog(\"Create Error => \" . $result . \" - \" . $product->errorsToString());__N__		$error++;__N__	} else {__N__		dol_syslog(\"Product id=\".$product->id.\" successfully\");__N__	}__N__	__N__	// Add langs, categories, versions and photos__N__	if (!$error && 1) {__N____N__		// Add alternative languages__N__		if ($object[\'multilangs\']) {__N__			foreach ($object[\'multilangs\'] as $key => $value) {__N__				$availableLangsShortCode = getAvailableLangs()[\'langs_code_shortcode\'];__N__				$product->multilangs[$availableLangsShortCode[$key]] = array(__N__					\'label\' => $value[\'name\'],__N__					\'description\' => $value[\'short_description\'],__N__					\'other\' => $value[\'description\'],__N__				);__N__			}__N__		}__N__		$product->multilangs;__N__		$ret = $product->setMultiLangs($user);__N__		if ($ret < 0) {__N__			dol_syslog(\"Error in setMultiLangs result code = \" . $ret . \" - \" . $product->errorsToString());__N__			$error++;__N__		} else {__N__			dol_syslog(\"Set MultiLangs OK\");__N__		}__N____N__		// Add owner of a module by adding supplier price__N__		$object_ProductFournisseur = new ProductFournisseur($db);__N__		$object_ProductFournisseur->fetch($product->id);__N__		$object_ProductFournisseur->add_fournisseur($user, $supplier->id, $product->ref, 1);__N__		$object_ProductFournisseur->update_buyprice(1, 0, $user, \'HT\', $supplier, 1, $supplier->ref, 20, 0, \'\', 0, 0, \'\', \'\', array(), \'\', 0, \'HT\');__N__		dol_syslog(\"Adding supplier prices (id_fourn=\".$supplier->id.\" ref_product_fourn=\".$product->ref.\") OK\");__N____N____N____N__		// Add  categories and versions__N__		if (!empty($object[\'version_min\']) && !empty($object[\'version_max\'])) {__N__			$version_min_object = new Categorie($db);__N__			$result = $version_min_object->fetch($object[\'version_min\']);__N__			$version_max_object = new Categorie($db);__N__			$result = $version_max_object->fetch($object[\'version_max\']);__N__			$version_tags = get_all_tags_between($version_min_object->label,$version_max_object->label);__N__			$version_ids = array();__N__			foreach ($version_tags as $key => $value) {__N__				$version_object = new Categorie($db);__N__				$result = $version_object->fetch(\'\', $value);__N__				$version_ids[$version_object->id] = $version_object->id;__N__			}__N__		}__N__		if (!empty($object[\'categories\'])) {__N__			if (!empty($version_ids)) {__N__				$object[\'categories\'] = array_merge($version_ids, $object[\'categories\']); // Add versions to categories__N__			}__N__			$ret_cat = $product->setCategories($object[\'categories\']);__N__			if ($ret_cat < 0) {__N__				dol_syslog(\"Error in setCategories result code = \" . $ret_cat . \" - \" . $product->errorsToString().\' \'.join(\',\', $object[\'categories\']));__N__				$error++;__N__			} else {__N__				dol_syslog(\"Set Categories and versions OK\");__N__			}__N__		}__N____N____N__		// Add pictures AND package__N__		$upload_dir = $conf->product->multidir_output[1] . \'/\' . get_exdir(0, 0, 0, 1, $product, \'product\');__N__		if (!is_dir($upload_dir)) {__N__			mkdir($upload_dir);__N__		}__N__		if (!empty($object[\'image_cover\'][\'name\'])) {__N__			$images[\'image_cover\'] = $object[\'image_cover\'];__N__		}__N__		if (!empty($object[\'other_images\'])) {__N__			$other_images = $object[\'other_images\'];__N__			foreach ($other_images as $key => $value) {__N__				$images[$key] = $value;__N__			}__N__		}__N__		if (!empty($images)) {__N__			foreach ($images as $key_image => $value_image) {__N__				$img = $upload_dir . \'/\' . $product->ref . \'-\' . $key_image . \'.jpg\';__N__	__N__				if (move_uploaded_file($value_image[\'tmp_name\'], $img) != true){__N__					$error++;__N__					dol_syslog(\"file copy impossible for the moment, please try again later\");__N__				} else {__N__					// Add thumb__N__					$product->addThumbs($img);__N__					__N__					$filename = basename($product->ref . \'-\' . $key_image  . \'.jpg\');__N__					$rel_dir = preg_replace(\'/^\' . preg_quote(DOL_DATA_ROOT, \'/\') . \'/\', \'\', $upload_dir);__N__					$rel_dir = preg_replace(\'/^[\\\\/]/\', \'\', $rel_dir);__N____N__					$ref = dol_hash($rel_dir . \'/\' . $filename, 3);__N____N__					// Add index to db__N__					if (empty ($id)) {__N__						addFileIntoDatabaseIndex($upload_dir, $filename, \'MARKETPLACE\', \'uploaded\', 1, $product);__N__					}__N__					__N__	__N__					// Update index to put position__N__					$ecmfile = new EcmFiles($db);__N__					$result_get_ecmfile = $ecmfile->fetch(\'\', $ref);__N__					if ($key_image == \'image_cover\'){__N__						$ecmfile->position = 0;__N__					} else {__N__						$position = preg_match(\'/_(\\d+)$/\', $key_image, $matches) ? $matches[1] : 100;__N__						$ecmfile->position = $position;__N__					}__N__					__N__					$result_update_ecmfile = $ecmfile->update($user);__N__	__N__					if ($result_update_ecmfile < 0) {__N__						dol_syslog(\"Error in set image \" . $key_image . \" result code = \" . $result_update_ecmfile . \" - \" . $ecmfile->errorsToString());__N__						$error++;__N__					} else {__N__						dol_syslog(\"Set Image \" . $key_image . \" OK\");__N__					}__N__				}__N__			}__N__		}		__N__		__N____N____N__		// Add package__N__		if (!empty($object[\'virtual_product_file\'][\'name\'])) {__N__			$package = $upload_dir . \'/\' . basename($object[\'virtual_product_file\'][\'name\']);__N____N__			if (move_uploaded_file($object[\'virtual_product_file\'][\'tmp_name\'], $package) != true){__N__				dol_syslog(\'Error: Failed to move uploaded file. \');__N__				$error++;__N__			} else {__N__				// Add index to db__N__				$filename = basename($object[\'virtual_product_file\'][\'name\']);__N__				addFileIntoDatabaseIndex($upload_dir, $filename, \'MARKETPLACE\', \'uploaded\', 1, $product);__N____N__				// Update index to put position__N__				$rel_dir = preg_replace(\'/^\' . preg_quote(DOL_DATA_ROOT, \'/\') . \'/\', \'\', $upload_dir);__N__				$rel_dir = preg_replace(\'/^[\\\\/]/\', \'\', $rel_dir);__N__				$ref = dol_hash($rel_dir . \'/\' . $filename, 3);__N____N__				$ecmfile = new EcmFiles($db);__N__				$result_get_ecmfile = $ecmfile->fetch(\'\', $ref);__N__				$ecmfile->position = 100;__N__				$result_update_ecmfile = $ecmfile->update($user);__N____N__				if ($result_update_ecmfile < 0) {__N__					dol_syslog(\"Error in setPackage result code = \" . $result_update_ecmfile . \" - \" . $ecmfile->errorsToString());__N__					$error++;__N__				} else {__N__					dol_syslog(\"Set Package OK\");__N__				}__N__			}__N__		}__N__	}__N____N____N__	if ($error == 0) {__N__		$db->commit();__N__		dol_syslog(\"Product id=\".$product->ref.\" successfully\");__N__		return 1;__N__	} else {__N__		$db->rollback();__N__		dol_syslog(\'--- end error nb=\' . $error);__N__		return $weblangs->trans(\"errorOccurred\");__N__	}__N__}__N____N__function get_all_tags_between($start, $end) {__N__    // Extraire les parties numériques des chaînes__N__    $start_num = intval(substr($start, 1));__N__    $end_num = intval(substr($end, 1));__N__    __N__    // Initialiser le tableau des résultats__N__    $result = array();__N__    __N__    // Générer les tags entre les deux valeurs__N__    for ($i = $start_num; $i <= $end_num; $i++) {__N__        $result[] = \'V\' . $i;__N__    }__N__    __N__    return $result;__N__}__N____N__// Send an email__N__function marketplace_send_email($id_template, $object, $to, $additionalSubstitutionArray) {__N__    global $db, $conf, $user;__N__    __N__    $subject = \'\';__N__	$msg = \'\';__N____N__	// Send email__N__	include_once DOL_DOCUMENT_ROOT.\'/core/class/html.formmail.class.php\';__N__	$formmail = new FormMail($db);__N__	// Set output language__N__	$outputlangs = new Translate(\'\', $conf);__N__	$outputlangs->setDefaultLang($_SESSION[\'lang\'][\'code\']);__N__	// Load traductions files required by page__N__	$outputlangs->loadLangs(array(\"main\", \"website\", \"marketplace@marketplace\"));__N____N__	// Get email content from template__N__	$arraydefaultmessage = null;__N____N__	$arraydefaultmessage = $formmail->getEMailTemplate($db, \'all\', $user, $outputlangs, $id_template, 1, \'\');__N____N__	if (!empty($id_template) && is_object($arraydefaultmessage) && $arraydefaultmessage->id > 0) {__N__		$subject = $arraydefaultmessage->topic;__N__		$msg     = $arraydefaultmessage->content;__N__	}__N____N__    //$substitutionarray = $additionalSubstitutionArray;__N__	$substitutionarray = getCommonSubstitutionArray($outputlangs, 0, null, $object);__N__	$substitutionarray = array_merge($substitutionarray, $additionalSubstitutionArray);__N__	complete_substitutions_array($substitutionarray, $outputlangs, $object);__N__	$subjecttosend = make_substitutions($subject, $substitutionarray, $outputlangs);__N__	$texttosend = make_substitutions($msg, $substitutionarray, $outputlangs);__N____N____N__	$moreinheader = \'X-Dolibarr-Info: send_an_email by Marketplace\'.\"\\r\\n\";__N____N__	//$result = $customer->sendEmail($texttosend, $subjecttosend, array(), array(), array(), \"\", \"\", 0, -1, \'\', $moreinheader);__N__	__N__	__N__	// Detect if message is HTML__N__	if (dol_textishtml($texttosend, 0)) {__N__		$texttosend = dol_htmlentitiesbr($texttosend);__N__	}__N____N____N__	// Envoi mail confirmation__N__	$from = $conf->email_from;__N__	if (getDolGlobalString(\'MAIN_MAIL_EMAIL_FROM\')) {__N__		$from = getDolGlobalString(\'MAIN_MAIL_EMAIL_FROM\');__N__	}__N____N__	$trackid = \'conf\'.$object->id;__N____N__	// Send email (substitutionarray must be done just before this)__N__	include_once DOL_DOCUMENT_ROOT.\'/core/class/CMailFile.class.php\';__N__	$mailfile = new CMailFile($subjecttosend, $to, $from, $texttosend, array(), array(), array(), \'\', \'\', 0, -1, \'\', \'\', $trackid, $moreinheader);__N__	if ($mailfile->sendfile()) {__N__		return 1;__N__	} else {__N__		return 0;__N__	}__N__}__N____N__function marketplace_check_discount($id_product) {__N__    global $db;__N__    __N__    include_once DOL_DOCUMENT_ROOT . \'/categories/class/categorie.class.php\';__N____N__    $discount = 0;__N__    $mode = \"\";__N__    __N__    $canBeDiscounted = 1;__N__    // Check if this product is allowed for discount__N__    $noDiscountProductCat = getDolGlobalInt(\"MARKETPLACE_DISCOUNT_EXCLUDE_PRODUCTS_CATEGORY_ID\");__N__    if (!empty($noDiscountProductCat)) {__N__        $category = new Categorie($db);__N__        $prod_categories = $category->getListForItem($id_product, \'product\');__N____N__        foreach ($prod_categories as $prod_categorie) {__N__            if (!empty($prod_categorie[\'id\']) && $prod_categorie[\'id\'] == $noDiscountProductCat) {__N__                $canBeDiscounted = 0;__N__                break;__N__            }__N__        }__N__    }__N__    __N__    // Check discount for preferred members__N__    if ($canBeDiscounted == 1 && !empty($_SESSION[\'user\'][\'is_preferred_member\']) && $_SESSION[\'user\'][\'is_preferred_member\'] == 1) {__N__        $discountValue = getDolGlobalInt(\"MARKETPLACE_PROSPECTCUSTOMER_PREFERRED_DISCOUNT\");__N__        if (!empty($discountValue)) {__N__            $discount = $discountValue;__N__            $mode = \"percent\";__N__        }__N__    }__N__    __N__    // TODO Check discount for other cases__N__    __N__    return array(__N__        \'value\' => $discount,__N__        \'mode\' => $mode__N__    );__N__}__N____N__function marketplace_calculate_discounted_price($price, $discount) {__N__    $discounted_price = $price;__N__    if ($discount[\'value\'] && $discount[\'mode\']) {__N__        if ($discount[\'mode\'] === \"percent\") {__N__            $discounted_price = $price * ((1 - $discount[\'value\'] / 100));__N__        }__N__        __N__        if ($discount[\'mode\'] === \"amount\") {__N__            $discounted_price = $price - $discount[\'value\'];__N__        }__N__    }__N__    __N__    return $discounted_price;__N__}__N____N__function marketplaceNotifySellersBuyerByEmail ($order_id) {__N__	global $db;__N____N__	// Get order__N__	require_once DOL_DOCUMENT_ROOT.\"/commande/class/commande.class.php\";__N__	$order = new Commande($db);__N__	$result = $order->fetch($order_id);__N____N__	// Get customer__N__	$customer = new SocieteAccount($db);__N__	$result = $customer->fetch($_SESSION[\'user\'][\'id_user\']);__N____N__	$products_list = \'<ol>\';__N____N__	// Find sellers__N__	$sql_get_order_products = \"select cd.fk_product, cd.qty __N__	from \".MAIN_DB_PREFIX.\"commandedet  cd __N__	where cd.fk_commande =\" .$order_id;__N__	if ($result_commande_det = $db->query($sql_get_order_products)) {__N__		while ($commande_det = $result_commande_det->fetch_object()) {__N__			// Get product__N__			$product = new Product($db);__N__			$result = $product->fetch($commande_det->fk_product);__N____N__			// Add product to list__N__			$products_list .= \'<li>\'.$product->label.\' (x\'.$commande_det->qty.\')</li>\';__N____N__			// Get product seller__N__			$sql_get_seller_id = \"select lpfp.fk_soc __N__				from \".MAIN_DB_PREFIX.\"product_fournisseur_price lpfp __N__				where lpfp.fk_product =\" .$product->id. \" LIMIT 1\";__N__			$resql = $db->query($sql_get_seller_id);__N__			$objsql = $db->fetch_object($resql);__N__			__N__			if(!empty($objsql->fk_soc)){__N__				// Get seller email__N__				$societeaccount = new SocieteAccount($db);__N__				$result = $societeaccount->fetch($objsql->fk_soc);__N__				$seller_email = $societeaccount->login;__N____N__				// Get seller name__N__				$seller = new Societe($db);__N__				$result = $seller->fetch($objsql->fk_soc);__N__				$seller_name = $seller->name;__N__				__N__				if ($result > 0) {__N__					$substitutionarray = array(__N__						\'__SELLER_NAME__\' => $seller_name,__N__						\'__BUYER_NAME__\' => strtoupper($_SESSION[\'user\'][\'pseudo\']),__N__						\'__ORDER_REF__\' => $order->ref,__N__						\'__ORDER_DATE__\' => dol_print_date($order->date, \'day\'),__N__						\'__PRODUCT__\' => $product->label,__N__						\'__QTY__\' => $commande_det->qty__N__					);__N__					//marketplace_send_email(150, $societeaccount, $seller_email, $substitutionarray);__N__					marketplace_send_email(getDolGlobalInt(\"MARKETPLACE_SELLERS_ORDER_CONFIRMATION_TEMPLATE\"), $societeaccount, \"daoud.mouhamed@gmail.com\", $substitutionarray);__N__				}__N__			}__N__		}__N__	}__N__	$products_list .= \"</ol>\";__N__	__N__	// Send buyer email__N__	$substitutionarray = array(__N__		\'__BUYER_NAME__\' => strtoupper($_SESSION[\'user\'][\'pseudo\']),__N__		\'__ORDER_REF__\' => $order->ref,__N__		\'__ORDER_DATE__\' => dol_print_date($order->date, \'day\'),__N__		\'__PRODUCTS__\' => $products_list__N__	);__N__	//marketplace_send_email(151, $customer, $customer->login, $substitutionarray);__N__	return marketplace_send_email(getDolGlobalInt(\"MARKETPLACE_BUYER_ORDER_CONFIRMATION_TEMPLATE\"), $customer, \"daoud.mouhamed@gmail.com\", $substitutionarray);__N__}__N____N__?>', '', 0);

