_$product_id = GETPOST(\'product_id\', \'int\');__N__$product_thumb = GETPOST(\'product_thumb\');__N__$product_qty = GETPOST(\'product_quantity\', \'int\');__N____N__if($product_id && $product_qty) {__N____N__    /*foreach($_POST as $key => $value){__N__        $new_product[$key] = $value;__N__    }*/__N____N__	$object = new Product($db);__N__	$get_product = $object->fetch($product_id);__N__	__N__	// check discount and calculate discounted price__N__    $discount = marketplace_check_discount($object->id);__N__    $discounted_price = marketplace_calculate_discounted_price($object->price_ttc, $discount);__N__    __N__    if ($discounted_price != $object->price_ttc) {__N__        $price_to_display = $discounted_price;__N__    } else {__N__        $price_to_display = $object->price_ttc;__N__    }__N____N__    $new_product[\'product_id\'] = $object->id;__N__    $new_product[\'product_thumb\'] = $product_thumb;__N__    $new_product[\'product_name\'] = $object->label;__N__    $new_product[\'product_ref\'] = $object->ref;__N__    $new_product[\'product_price\'] = $price_to_display;__N__    $new_product[\'product_qty\'] = $product_qty;__N____N__        if(isset($_SESSION[\'products\'])){  //if session var already exist__N____N__            if(isset($_SESSION[\'products\'][$new_product[\'product_id\']])) //check item exist in products array__N__            {__N__                unset($_SESSION[\'products\'][$new_product[\'product_id\']]); //unset old item__N__            }__N__        }__N____N__        $_SESSION[\'products\'][$new_product[\'product_id\']] = $new_product;	//update products with new item array__N____N__    $total_items = count($_SESSION[\'products\']); //count total items__N____N__    $popup_product_html = \'__N__        <span class=\"cross\" title=\"Close window\"></span>__N__        <h2>__N__            <i class=\"icon-ok\"></i>Product successfully added to your shopping cart__N__        </h2>__N__        <div class=\"product-image-container layer_cart_img\">__N__            <img class=\"layer_cart_img img-responsive\" src=\"\'.$product_thumb.\'\" alt=\"Subtotal V3\" title=\"\'.$object->label.\'\">__N__        </div>__N__        <div id=\"layer_cart_product_info\">__N__            <span id=\"layer_cart_product_title\" class=\"product-name\">\'.$object->label.\'</span>__N__            <span id=\"layer_cart_product_attributes\"></span>__N__            <div>__N__            <strong class=\"dark\">Quantity</strong>__N__            <span id=\"layer_cart_product_quantity\">\'.$product_qty.\'</span>__N__            </div>__N__            <div>__N__            <strong class=\"dark\">Total</strong>__N__            <span id=\"layer_cart_product_price\">(\'.($product_qty * $price_to_display).\' €)</span>__N__            </div>__N__        </div>\';__N____N__    die(json_encode(array(\'items\'=>$total_items,\'popup_product_html\'=>$popup_product_html))); //output json__N____N__}__N____N__if(isset($_POST[\'load_cart\']) && $_POST[\'load_cart\'] == 1)__N__{__N__    if(isset($_SESSION[\'products\']) && count($_SESSION[\'products\'])>0){ //if we have session variable__N__        $cart_box = \'\';__N__        $total = 0;__N__        foreach($_SESSION[\'products\'] as $cart_product){ //loop though items and prepare html content__N____N__            $cart_box .= \"<dl class=\\\"products\\\">__N__                <dt class=\\\"item\\\" data-id=\\\"cart_block_product_\" . $cart_product[\'product_id\'] . \"\\\" style=\\\"display: block;\\\">__N__                    <a class=\\\"cart-images\\\" href=\\\"product.php?id=\" . $cart_product[\'product_id\'] . \"\\\" title=\\\"\" . $cart_product[\'product_name\'] . \"\\\"><img src=\\\"\" . $cart_product[\'product_thumb\'] . \"\\\" alt=\\\"\" . $cart_product[\'product_name\'] . \"\\\"></a>__N__                    <div class=\\\"cart-info\\\">__N__                        <div class=\\\"product-name\\\"><span class=\\\"quantity-formated\\\"><span class=\\\"quantity\\\">\" . $cart_product[\'product_qty\'] . \"</span>&nbsp;x&nbsp;</span><a href=\\\"product.php?id=\" . $cart_product[\'product_id\'] . \"\\\" title=\\\"\" . $cart_product[\'product_name\'] . \"\\\" class=\\\"cart_block_product_name truncate3\\\">\" . $cart_product[\'product_name\'] . \"</a></div>__N__                        <span class=\\\"price\\\">\" . price($cart_product[\'product_price\']) . \" €</span>__N__                    </div>__N__                    <span class=\\\"remove_link\\\"><a rel=\\\"nofollow\\\" class=\\\"ajax_cart_block_remove_link\\\" href=\\\"\\\" data-code=\\\"\" . $cart_product[\'product_id\'] . \"\\\"> </a></span>__N__                </dt>__N__            </dl>\";__N____N__            $subtotal = ($cart_product[\'product_price\'] * $cart_product[\'product_qty\']);__N__            $total = ($total + $subtotal);__N__        }__N__        $cart_box .= \'__N__        <div class=\"cart-prices\">__N__            <div class=\"cart-prices-line first-line\">__N__                <span class=\"price cart_block_shipping_cost ajax_cart_shipping_cost\"> Free shipping! </span>__N__                <span> Shipping </span>__N__            </div>__N__            <div class=\"cart-prices-line last-line\">__N__                <span class=\"price cart_block_total ajax_block_cart_total\">\' . $total . \' €</span>__N__                <span>Total</span>__N__            </div>__N__            <p> Prices may vary depending on your country. </p>__N__        </div>__N__        <p class=\"cart-buttons\">__N__            <a id=\"button_order_cart\" class=\"btn btn-default button button-small\" href=\"\' . $order_page_url . \'\" title=\"Check out\" rel=\"nofollow\">__N__                <span> Check out <i class=\"icon-chevron-right right\"></i>__N__                </span>__N__            </a>__N__        </p>\';__N__        die($cart_box); //exit and output content__N__    }else{__N__        die(\"<p class=\'cart_block_no_products\'> No products </p>\"); //we have empty cart__N__    }__N__}__N____N__if(isset($_POST[\'load_popup_products_html\']) && $_POST[\'load_popup_products_html\'] == 1)__N__{__N__    if(isset($_SESSION[\'products\']) && count($_SESSION[\'products\'])>0){ //if we have session variable__N__        $total = 0;__N__        foreach($_SESSION[\'products\'] as $cart_product){ //loop though items and prepare html content__N__            $subtotal = ($cart_product[\'product_price\'] * $cart_product[\'product_qty\']);__N__            $total = ($total + $subtotal);__N__        }__N__        if(count($_SESSION[\'products\']) == 1){__N__            $title = \'<span class=\"ajax_cart_product_txt\"> There is 1 item in your cart. </span>\';__N__        }else{__N__            $title = \'<span class=\"ajax_cart_product_txt_s\"> There are <span class=\"ajax_cart_quantity\">\'.count($_SESSION[\'products\']).\'</span> items in your cart. </span>\';__N__        }__N__        $popup_products_html = \'__N__        <h2>\'.$title.\'</h2>__N__        <div class=\"layer_cart_row\">__N__            <strong class=\"dark\"> Total products (tax incl.) </strong>__N__            <span class=\"ajax_block_products_total\">\'.$total.\' €</span>__N__        </div>__N__        <div class=\"layer_cart_row\">__N__            <strong class=\"dark\"> Total shipping&nbsp;(tax incl.) </strong>__N__            <span class=\"ajax_cart_shipping_cost\"> Free shipping! </span>__N__        </div>__N__        <div class=\"layer_cart_row\">__N__            <strong class=\"dark\"> Total (tax incl.) </strong>__N__            <span class=\"ajax_block_cart_total\">\'.$total.\' €</span>__N__        </div>__N__        <div class=\"button-container\">__N__            <span class=\"continue btn btn-default button exclusive-medium\" title=\"Continue shopping\">__N__            <span>__N__                <i class=\"icon-chevron-left left\"></i>Continue shopping </span>__N__            </span>__N__            <a class=\"btn btn-default button button-medium\" href=\"\' . $order_page_url . \'\" title=\"Proceed to checkout\" rel=\"nofollow\">__N__            <span> Proceed to checkout <i class=\"icon-chevron-right right\"></i>__N__            </span>__N__            </a>__N__        </div>\';__N____N__        die($popup_products_html); //exit and output content__N__    }__N__}__N____N____N__//Remove item from shopping cart__N__if(isset($_GET[\'remove_code\']) && isset($_SESSION[\'products\']))__N__{__N__    $product_code   = $_GET[\'remove_code\']; //get the product code to remove__N____N__    if(isset($_SESSION[\'products\'][$product_code]))__N__    {__N__        unset($_SESSION[\'products\'][$product_code]);__N__    }__N____N__    $total_items = count($_SESSION[\'products\']);__N__    die(json_encode(array(\'items\'=>$total_items)));__N__}__N____N__if(isset($_POST[\'action\']) && $_POST[\'action\'] == \"update_order\" && isset($_POST[\'order\'])){__N__    include_once DOL_DOCUMENT_ROOT . \'/ecm/class/ecmfiles.class.php\';__N__    $error = 0;__N__    foreach ($_POST[\'order\'] as $id_image => $position_image) {__N__        $ecmfile = new EcmFiles($db);__N__        $result_get_ecmfile = $ecmfile->fetch($id_image);__N__        $ecmfile->position = $db->escape($position_image);__N__        $result_update_ecmfile = $ecmfile->update($user);__N__        if ($result_update_ecmfile < 0) {__N__		    $error++;__N__    	}__N__    }    __N__    if ($error > 0) {__N__        die(json_encode(array(\'success\' => false, \'message\' => $langs->trans(\'errorOccurred\'))));__N__	} else {__N__		die(json_encode(array(\'success\' => true, \'message\' => $langs->trans(\'orderChanged\'))));__N__	}__N____N__}__N____N__if ($_SERVER[\'REQUEST_METHOD\'] == \'POST\' && isset($_POST[\'action\']) && $_POST[\'action\'] == \'add_product_image\' && isset($_POST[\'position\']) && isset($_POST[\'product_id\']) && isset($_FILES[\'file\'][\'name\'])) {__N__    includeContainer(\'_library_page_lib.php\');__N__    __N__    $error = 0;__N__    $error_messages = \'\';__N__    $product = new Product($db);__N__	$r